INSERT INTO `posts` (`id`, `category_id`, `author_id`, `title`, `content`, `published_at`, `status`, `created_at`, `updated_at`, `image`, `is_featured`, `total_hit`) VALUES
(1, 2, 3, 'কেন্দ্রীয় নয়, গুচ্ছ ভিত্তিতেই বিশ্ববিদ্যালয়ের ভর্তি পরীক্ষা', 'কেন্দ্রীয় নয়, সাত কৃষি বিশ্ববিদ্যালয়ের মতো অন্য বিশ্ববিদ্যালয়গুলোতেও গুচ্ছ ভিত্তিতে ভর্তি পরীক্ষা অনুষ্ঠিত হবে। সমমনা বিশ্ববিদ্যালয়গুলোকে চারটি গুচ্ছে ভাগ করে এই ভর্তি পরীক্ষা নেওয়া হবে। এর মধ্যে বিজ্ঞান ও প্রযুক্তি বিশ্ববিদ্যালয়গুলোকে একটি গুচ্ছ করে, প্রকৌশল বিশ্ববিদ্যালয়গুলোর জন্য একটি, কৃষি বিশ্ববিদ্যালয়গুলোর জন্য একটি এবং সাধারণ বিশ্ববিদ্যালয়গুলোর জন্য আরেকটি গুচ্ছ করে এই ভর্তি পরীক্ষা হবে।\r\n\r\nআজ বুধবার রাজধানীর আগারগাঁওয়ে বিশ্ববিদ্যালয় মঞ্জুরি কমিশনের (ইউজিসি) কার্যালয়ে উপাচার্যদের নিয়ে অনুষ্ঠিত সভায় এই সিদ্ধান্ত হয়। সভা শেষে ইউজিসির চেয়ারম্যান কাজী শহীদুল্লাহ এক সংবাদ ব্রিফিংয়ে এসব তথ্য জানান।\r\n\r\nতবে ১৯৭৩–এর আদেশে চলা চার বিশ্ববিদ্যালয় ঢাকা, জাহাঙ্গীরনগর, রাজশাহী ও চট্টগ্রাম এবং বাংলাদেশ প্রকৌশল বিশ্ববিদ্যালয় (বুয়েট) আগেই জানিয়েছে, তারা ইউজিসির কেন্দ্রীয় পরীক্ষায় অংশ নিচ্ছে না', '2020-02-27', 'published', '2020-02-26 09:12:16', '2020-03-01 09:07:05', 'images/upload/post/eyJpdiI6InZHd1E3YWNxcjVUbnM1bkM1cndUSWc9PSIsInZhbHVlIjoibmZcLzJYRmNrSWZsbnk0aWFYMzBKa0U3TjdpeTdLXC9UZjFEWHp3QjBra0JFPSIsIm1hYyI6IjEyYTk4MDJhOTEzMDhhZGQxYThjNDYzMDY3ZWNhYTdkNjY3YmNjZTkzNDk1OWI0NjgxZTI5NWNlYmY3YTE2ODUifQ==.jpg', 0, 1),
(2, 1, 1, 'যাত্রাবাড়ীতে কিশোর ইমন হত্যায় তিন কিশোরের স্বীকারোক্তি', 'মামলার কাগজপত্র, নিহতের পরিবার ও পুলিশের সঙ্গে কথা বলে জানা গেছে, কিশোর ইমন তার বাবার সঙ্গে যাত্রাবাড়ীর একটি দোকানে দরজির কাজ করত। গত রোববার রাত আটটার দিকে ইমনের পায়ের ওপর এক (আসামিদের বন্ধু) ছেলে সাইকেলের চাকা তুলে দেয়। ক্ষিপ্ত হয়ে তখন ইমন ওই ছেলেকে থাপ্পড় মারে। পরে ওই ছেলে তার বন্ধুদের এই কথা জানায়। গতকাল দুপুরে কাজ শেষে বাসায় ফেরার পথে গোবিন্দপুর সরকারি প্রাথমিক বিদ্যালয়ের সামনে ইমনকে সাত থেকে আট কিশোর ঘিরে ধরে। এরপর তারা তাকে এলোপাতাড়ি মারতে থাকে। একপর্যায়ে এক কিশোর চাকু দিয়ে ইমনের বুকে আঘাত করে। তখনই মাটিতে লুটিয়ে পড়ে ইমন। পরে আসামিরা ঘটনাস্থল থেকে পালিয়ে যায়।\r\n\r\n\r\nমামলার তদন্ত কর্মকর্তা যাত্রাবাড়ী থানার উপপরিদর্শক শরিফুল ইসলাম প্রথম আলোকে বলেন, তুচ্ছ কারণে একদল কিশোর আরেক কিশোর ইমনকে ছুরিকাঘাত করে হত্যা করেছে। তিন কিশোরকে বিচারের আওতায় আনা সম্ভব হয়েছে। বাকি দুজনকে ধরার চেষ্টা চলছে। শরিফুল ইসলাম বলেন, অভিযুক্ত কিশোরেরা সবাই স্কুলছাত্র। তুচ্ছ কারণে ইমনকে তারা হত্যা করেছে।\r\n\r\nনিহত ইমনের ফুফু ফারজানা প্রথম আলোকে বলেন, ‘আমার ভাতিজা ইমনকে বিনা দোষে ওরা হত্যা করেছে। আমরা আসামিদের সর্বোচ্চ শাস্তি চাই।’', '2020-02-27', 'published', '2020-02-26 09:46:54', '2020-02-26 20:33:09', 'images/upload/post/eyJpdiI6ImtReXkyc2lCdWE5OWRjR1RCSjdlc2c9PSIsInZhbHVlIjoiZ3QzNitrWDFjXC9FRGEyK0JpbjlLZFwvSytoRFMyNG5BTmV4RUdCRlp3ZHBJPSIsIm1hYyI6ImY3NTAxMzQ3MDY3ZjIyNmRiOGI3MzcwOTU0ZDIxNjlkM2Q5NGFlZTBhMzI4ZmQ0MTFlMzcyY2FkYzJkYmNkZmYifQ==.jpg', 0, 0),
(3, 8, 1, 'যশোর শহরে চাকু মেরে যুবককে খুন', 'পুলিশ ও নিহতের পরিবারের স্বজনদের সূত্রে জানা গেছে, ইমরান মাছ বাজার মোড়ে চায়ের দোকানে দাঁড়িয়েছিলেন। এ সময় তিন-চারজন দুর্বৃত্ত অতর্কিত সেখানে গিয়ে ইমরানের বুকে-পেটে চাকু মেরে পালিয়ে যায়। স্থানীয় লোকজন গুরুতর অবস্থায় তাঁকে উদ্ধার করে যশোর জেনারেল হাসপাতালে নিয়ে যান। হাসপাতালে নেওয়ার কিছুক্ষণ পরে তিনি মারা যান।\r\n\r\n\r\nনিহত ইমরানের চাচা শেখ মতিয়ার রহমান বলেন, ‘ইমরান আমার আড়তদারি ব্যবসা দেখাশোনা করত। পাওনাদারদের কাছে টাকা চাইতে সন্ধ্যায় সে দোকান থেকে বেরিয়ে যায়। কিছুক্ষণ পরে খবর আসে স্থানীয় পলাশসহ কয়েকজন তাঁকে চাকু মেরেছে। কয়েক দিন আগে পলাশ কারাগার থেকে বের হয়েছে। পলাশের সঙ্গে ইমরানের কোনো বিরোধ ছিল কি না, আমার জানা নেই।’\r\n\r\nযশোরের অতিরিক্ত পুলিশ সুপার তৌহিদুল ইসলাম বলেন, ‘হত্যাকাণ্ডের পর ঘটনাস্থলে পুলিশ পাঠানো হয়। হত্যার সঙ্গে জড়িত কয়েকজনের নাম-ঠিকানা পাওয়া গেছে। কী কারণে তাকে হত্যা করা হয়েছে, তদন্ত করে দেখা হচ্ছে। ধারণা করা হচ্ছে, পূর্বের কোনো দ্বন্দ্বের কারণে দুর্বৃত্তরা ইমরানকে হত্যা করতে পারে।’', '2020-03-31', 'published', '2020-02-26 10:08:59', '2020-11-12 04:03:16', 'images/upload/post/eyJpdiI6InJIMmhIOEx0MzdIMWlzTnVIXC9OUXJBPT0iLCJ2YWx1ZSI6IkptckdXTnMyU1BkU015WVNUNEVqRjRFNmI1RjJGanZ3UWZOdDZXbUNySTg9IiwibWFjIjoiNDc4Y2QzOGIxZDA2N2QzMjg3ODA5NzViNGM2ZDk5NjU1MmNkYmEwM2RhMTE1MTYwZjRiNzEyNDk2Nzg0M2IzYSJ9.jpg', 1, 31),
(5, 1, 3, 'মিরাজের বাসায় ‍চুরি, খোয়া গেছে মা ও স্ত্রীর ২৭ ভরি স্বর্ণ', 'রাজধানীর মিরপুরে জাতীয় দলের ক্রিকেটার মেহেদী হাসান মিরাজের ফ্ল্যাটে চুরির ঘটনা ঘটেছে।\r\n\r\nদুর্বৃত্তরা সোনার অলংকার ও ডলার চুরি করে নিয়ে গেছে।\r\n\r\nএ ঘটনায় কাফরুল থানায় একটি মামলা হয়েছে।\r\n\r\nপারিবারিক সূত্রে জানা গেছে, মিরাজের মায়ের ৭ ভরি আর স্ত্রীর ২০ ভরি স্বর্ণালংকার চুরি হয়েছে। যেটির বর্তমান বাজারমূল্য প্রায় সাড়ে ১৬ লাখ টাকা। সোনার সঙ্গে চুরি হয়েছে ৬ হাজার মার্কিন ডলার, বাংলাদেশি মুদ্রায় যেটি ৫ লাখ ১০ হাজার টাকা।\r\n\r\nমিরাজ থাকেন মিরপুরে বিজয় রাকিন সিটির একটি ফ্ল্যাটে। জিম্বাবুয়ের বিপক্ষে মিরপুর টেস্ট চলায় দলের সব খেলোয়াড়ের মতো তাকেও থাকতে হয়েছে টিম হোটেলে। হোটেলে মিরাজের সঙ্গে ছিলেন স্ত্রী রাবেয়া আখতারও।\r\n\r\nগত পাঁচ দিন তার ফ্ল্যাট ছিল ফাঁকা। আজ দুজন ফিরে দেখেন চুরি হয়েছে বাসায়।\r\n\r\nপুলিশ জানায়, গত ২০ ফেব্রুয়ারি খেলার জন্য বাসা থেকে হোটেলে যান মিরাজ। খেলা শেষে আজ বুধবার বাসায় ফিরে দেখেন ঘরের তালা ভাঙা। ভেতরে সব তছনছ করে ২৫ ভরি সোনার অলংকার চুরি হয়েছে। তার বাসা থেকে ৬০০ ডলার খোয়া গেছে। বাড়িটির ১০ তলায় পরিবার নিয়ে বসবাস করেন মিরাজ। গত ২০ ফেব্রুয়ারি স্ত্রীসহ চলে যান তিনি। ফলে তার ফ্ল্যাটটি খালি ছিল।\r\n\r\nকাফরুল থানার ওসি সেলিমুজ্জামান বলেন, ‘ঘটনাটি কীভাবে কারা ঘটিয়েছে তদন্ত করে দেখা হচ্ছে।’\r\n\r\nএ বিষয়ে মিরাজ সাংবাদিকদের জানান, ‘কীভাবে যে ঘটনাটা ঘটল কিছুই বুঝতে পারছি না। আমি টিম হোটেল থেকে বাসায় ঢুকে দেখি সবকিছু তছনছ।’', '2020-02-28', 'published', '2020-02-26 20:18:07', '2020-04-28 07:10:04', 'images/upload/post/eyJpdiI6IkdyXC9tUjdYTmZoWVZBWWtqTzVxWmJRPT0iLCJ2YWx1ZSI6IjhYcGlaR1ZvbHI0Q3k1NEF4WnNkbm9NK2h2K25ieEczeUg2c1lTb3ZXbkE9IiwibWFjIjoiZDNlNDE2MWFkN2EzZWNkNGQ4MDAxZDU1NWE4ZTc4NTAwZDhhNGFiNTk0NWJlZDVhMzgyMDRjMWViMDI5ZTkyNSJ9.jpg', 1, 3),
(10, 1, 4, 'বিদ্যুতের দাম বাড়ায় ‘হোঁচট খাবে রপ্তানি’', 'বিশ্ববাজারে প্রতিযোগিতায় টিকে থাকতে হিমশিম খাওয়া দেশের রপ্তানি খাতের উপর বিদ্যুতের দাম বৃদ্ধি ‘মরার উপর খাঁড়ার ঘা’ হিসেবে দেখা দিতে পারে বলে শঙ্কা প্রকাশ করেছেন শিল্পোদ্যোক্তারা।\r\n\r\nতারা বলছেন, এমনিতেই আন্তর্জাতিক বাজারে পণ্যের দাম ধারাবাহিকভাবে কমছে। সেখানে বিদ্যুতের দাম বাড়লে পণ্যের উৎপাদন খরচ বেড়ে যাবে। তখন বাংলাদেশি উদ্যোক্তারা প্রতিযোগিতায় পিছিয়ে পড়বে।\r\n\r\nবিদ্যুৎ উৎপাদন ও বিতরণ কোম্পানিগুলোর নানা অনিয়ম, সিস্টেম লস ও অদক্ষতার কারণে যে ক্ষতি তা পোষাতে গ্রাহকের উপর বাড়তি দামের বোঝা চাপানোর যৌক্তিকতা নিয়ে প্রশ্ন তুলেছেন তারা।\r\n\r\nবিজিএমইএ সভাপতি রুবানা হকবিজিএমইএ সভাপতি রুবানা হকদেশের রপ্তানি আয়ের প্রধান খাত তৈরি পোশাকের শিল্পমালিকদের সংগঠন বিজিএমইএর সভাপতি রুবানা হক বিডিনিউজ টোয়েন্টিফোর ডটকমকে বলেন, গত চার বছরে ধাপে ধাপে পোশাক খাতে উৎপাদন ব্যয় ২৯ দশমিক ৪০ শতাংশ বেড়ে গেল। উল্টোদিকে বাজারে পণ্যের দাম ধারাবাহিকভাবে হ্রাস পাচ্ছে।\r\n“এই পরিস্থিতিতে বিদ্যুতের মূল্য বৃদ্ধি কিংবা অন্য যে কোনো পর্যায়ে উৎপাদন ব্যয় বৃদ্ধির ধাক্কা সহ্য করার মতো সক্ষমতা পোশাক শিল্পের নেই।”\r\n\r\nরপ্তানি আয়ে ফের ধাক্কা\r\n\r\nরপ্তানি উন্নয়ন ব্যুরোর (ইপিবি) হিসাবে, ২০১৮-১৯ অর্থবছরে দেশের রপ্তানি আয় ছিল ৪ হাজার ৫৩ কোটি ৫০ লাখ (৪০.৫৩ বিলিয়ন) ডলারের বেশি, যা আগের অর্থবছরের চেয়ে সাড়ে ১০ শতাংশ বেশি। এই আয়ের ৮৪ শতাংশই এসেছে তৈরি পোশাক খাত থেকে।\r\n\r\nতবে চলতি অর্থবছরে রপ্তানি প্রবৃদ্ধি উল্টো পথে হাঁটছে। প্রথম সাত মাসে (জুলাই-জানুয়ারি) ২ হাজার ২৯২ কোটি ডলারের ডলারের পণ্য রপ্তানি করেছে বাংলাদেশ, যা গত অর্থবছরের একই সময়ের চেয়ে ৫ দশমিক ২১ শতাংশ কম।\r\n\r\nবৃহস্পতিবার খুচরা ও পাইকারি পর্যায়ে বিদ্যুতের নতুন যে মূল্যহার ঘোষণা করা হয়েছে, তাতে স্ল্যাব অনুযায়ী ক্ষুদ্র শিল্পে বিদ্যুৎ বিল ১৬ হাজার ৫৫০ টাকা থেকে বেড়ে ১৭ ৩৬০ টাকা, মাঝারি শিল্পের ৩ লাখ ২৬ হাজার টাকা থেকে ৩ লাখ ৪৪ হাজার টাকা ও বৃহৎ শিল্পে এক কোটি ৬২ লাখ ৪০ হাজার টাকা থেকে এক কোটি ৭১ লাখ ৪০ হাজার টাকা হবে বলে বাংলাদেশ এনার্জি রেগুলেটরি কমিশন-বিইআরসির চেয়ারম্যান একটি হিসাব দিয়েছেন।\r\n\r\nবিদ্যুতের দাম সব পর্যায়েই বাড়ল\r\n\r\nবিদ্যুতের দাম সব পর্যায়েই বাড়ল\r\n\r\nবিদ্যুতের দাম বাড়ানোর পর পরই বিএনপি ও বামদলগুলোসহ বিভিন্ন অঙ্গন থেকে প্রতিবাদ জানানো হয়েছে। মূল্য বৃদ্ধিকে অযৌক্তিক আখ্যা দিয়ে তা প্রত্যাহারের দাবি তুলেছে ভোক্তা অধিকার সংগঠন ক্যাব।\r\n\r\nবিদ্যুতের উৎপাদন বেড়ে চাহিদার তুলনায় প্রবৃদ্ধি হওয়ার পরেও দাম না কমে উল্টো বাড়ায় হতাশা প্রকাশ করেছেন বেঙ্গল প্লাস্টিকস ইন্ডাস্ট্রির অন্যতম কর্ণধার জসিম উদ্দিন।\r\n\r\nবিডিনিউজ টোয়েন্টিফোর ডটকমকে তিনি বলেন, “বিদ্যুতের এই মূল্যবৃদ্ধি প্লাস্টিক শিল্পে বড় ধরনের নেতিবাচক প্রভাব ফেলবে। এই মুহূর্তে ইন্টারন্যাশনাল মার্কেটটা খানিকটা ‘ভোলাটাইল’। এর সঙ্গে যোগ হল বিদ্যুতের বাড়তি দাম। এখন উৎপাদনের খরচ বাড়বে, ফলে প্লাস্টিক পণ্যের দাম বেড়ে যাবে।\r\n\r\n“আমরা পণ্যের মূল্য ও কোয়ালিটির ক্ষেত্রে বিশ্বের সাথে প্রতিযোগিতায় রয়েছি। এই পরিস্থিতিতে বিদ্যুতের মূল্য বৃদ্ধি সেই প্রতিযোগিতায় বাংলাদেশকে পিছিয়ে দেবে। কারণ উৎপাদন খরচ বাড়ার বিপরীতে বাজারে পণ্যের দাম বাড়ছে না, বরং কমছে। আস্তে আস্তে বাংলাদেশ বিশ্ববাজার থেকে আউট হয়ে যাবে।”\r\n\r\nবিদ্যুতের মূল্য বৃদ্ধির পেছনে সংশ্লিষ্ট দপ্তরগুলোর স্তরে স্তরে দুর্নীতির অভিযোগ করে আসছেন ভোক্তা নেতারা। বিষয়টি সরাসরি না উল্লেখ না করলেও কোম্পানির অনিয়ম, সিস্টেম লস ও অদক্ষতার প্রভাবে এ ধরনের মূল্য বৃদ্ধির ঘটনা ঘটছে বলে মনে করেন ব্যবসায়ীদের শীর্ষ সংগঠন এফবিসিসিআইয়ের এই সাবেক সহ-সভাপতি।', '2020-03-30', 'published', '2020-02-28 11:40:25', '2020-04-01 01:39:03', 'images/upload/post/eyJpdiI6Ind3TENmbXZkMWQrNWhUNURCdlwvZnRBPT0iLCJ2YWx1ZSI6Ikg2OWx6bnlJaDZ6Z2tTMUppeFRVWXRVV3VtRDFmcSt0NjI5ZDZhV0ptRTg9IiwibWFjIjoiOTczOTYyMGEwMTcyODVjZTBlMGM1OThkYzNjYzE5ZGY3MzcyYWRhNThhOWIxZGVjNTA2YTZkMTQwMWM4MzVkMSJ9.jpg', 1, 3),
(11, 5, 5, 'পশ্চিম তীরে ১২ ফিলিস্তিনি গ্রেফতার', 'দখলদার ইসরাইলি বাহিনী পশ্চিম তীরের বিভিন্ন এলাকা থেকে ১২ ফিলিস্তিনিকে গ্রেফতার করেছে। শুক্রবার সকালে তাদের আটক করা হয়।\r\n\r\nআটককৃতদের বিরুদ্ধে দখল প্রতিরোধ, বসতি স্থাপনকারীদের বিরুদ্ধে অভিযান চালানোর পরিকল্পনাসহ নানা অভিযোগ আনা হয়েছে। খবর আনাদলুর।\r\n\r\nঅন্যদিকে বৃহস্পতিবার ইসরাইলি কর্তৃপক্ষ গাজা ক্রসিং বন্ধ করার দুই দিনের মাথায় আবার তা খুলে দিয়ে মাছ ধরার জায়গা ১৫ মাইলে প্রসারিত করেছে।\r\n\r\nদখলদার বাহিনী জানিয়েছে, নিরাপত্তা পরিস্থিতি পুনর্বিবেচনা করার পরে বৃহস্পতিবার সকালে থেকে করম আবু সালেম এবং বিট হানুন ক্রসিংগুলো খুলে দেয়া হয়েছে এবং মাছ ধরার অঞ্চলটি ১৫ মাইল সম্প্রসারিত করার সিদ্ধান্ত নেয়া হয়েছে।\r\n\r\nপ্রসঙ্গত, ইসরাইলি বাহিনী বাণিজ্যিক গতিবিধির অজুহাতে করম আবু সালেম ক্রসিং এবং গাজা উপত্যকায় সাম্প্রতিক আগ্রাসনের অজুহাতে জেলেদের জন্য সমুদ্রকে পুরোপুরি বন্ধ করে দিয়েছিল।', '2020-03-01', 'published', '2020-02-29 22:16:41', '2020-03-01 13:07:04', 'images/upload/post/eyJpdiI6ImMzRDJCT0l1YXhQTHZDdUVxVGdSM2c9PSIsInZhbHVlIjoidnhxYU5Ndmd0ZFNNR3BtN05QZ00zdHRUSk5ab0ZFcVE4OUxtZEQySEliMD0iLCJtYWMiOiI4Yjc0N2YwYjkyZTUyZDhiM2E4YzNmYWVjYjE0NmQ1YWIwMTk0N2ZmZjI1ZmQ1Zjg2N2E3OTI1OTM2NDlmZDRhIn0=.jpg', 0, 1),
(12, 5, 5, 'অন্তঃসত্ত্বা বান্ধবীকে বিয়ের ঘোষণা ব্রিটিশ প্রধানমন্ত্রীর', 'ব্রিটিশ প্রধানমন্ত্রী বরিস জনসনের দীর্ঘদিনের বান্ধবী ক্যারি সাইমন্ডস অন্তঃসত্ত্বা। গতকাল শনিকার সঙ্গীকে বিয়ে করার ঘোষণা দিয়েছেন জনসন।\r\n\r\nসরকারি বাসভবন ও দফতর ১০ ডাউনিং স্ট্রিট থেকে এ কথা জানান তিনি।\r\n৫৫ বছর বয়সী জনসনের দ্বিতীয় স্ত্রীর ঘরে আরও চার সন্তান আছে।\r\n\r\nএর আগে ২০১০ সালে সর্বশেষ সন্তানের মুখ দেখেন ডেভিড ক্যামেরন।\r\n\r\nসাইমন্ডস দীর্ঘদিন ধরে জনসনের রাজনৈতিক সতীর্থ। ২০১২ সালে তাকে লন্ডনের মেয়র হিসেবে জিতিয়ে আনার প্রচারণায় নেতৃত্ব দেন। সূত্র: সিএনএন।', '2020-03-01', 'published', '2020-02-29 22:17:51', '2020-02-29 22:17:51', 'images/upload/post/eyJpdiI6IkFxY3VyMWthQnM4NWhhaXh3TGhpNEE9PSIsInZhbHVlIjoiNWMySkpQbWV1XC96d2o3VWh3YTRQM0JpR2NScFwvSWkxaURObUowTDdoYU5JPSIsIm1hYyI6ImFiODI4N2EwMzFiNGMyOGVmMWIyNTdiMTA2NzE4N2JmMDJmYjExYjM4MjE1ZTZlY2ZhZGNiYzI5ZmI0ZmUzNzcifQ==.jpg', 1, 0),
(13, 8, 4, 'সোনামসজিদে সিঅ্যান্ডএফ কার্যালয়ে ১০ দিন থেকে তালা', 'চাঁপাইনবাবগঞ্জের সোনামসজিদ স্থলবন্দর সিঅ্যান্ডএফ এজেন্ট অ্যাসোসিয়েশন কার্যালয়ে গত ২০ ফেব্রুয়ারি থেকে তালা ঝুলছে। অ্যাসোসিয়েশনের পূর্বের কমিটি ও প্রতিদ্বন্দ্বী অপর একটি কমিটি গঠন করে কার্যালয় দখল নিয়ে অপ্রীতিকর ঘটনার আশঙ্কায় তালা ঝুলিয়েছে কাস্টমস কর্তৃপক্ষ।\r\n\r\n\r\nসোনামসজিদ স্থলবন্দরে কাস্টমসে দায়িত্বরত সহকারী কমিশনার সাইফুর রহমান জানান, সিঅ্যান্ডএফ এজেন্ট অ্যাসোসিয়েশনের বর্তমানে দুটি কমিটি। একটি কমিটি বিদ্যমান থাকার পর অপর একটি কমিটি গঠিত হয়েছে।\r\n\r\nদুটি কমিটির মধ্যে কার্যালয়ের দখল নিয়ে যে কোনো মুহূর্তে অপ্রীতিকর ঘটনা ঘটতে পারে এ আশঙ্কায় কার্যালয়ে অতিরিক্ত একটি তালা দেয়া হয়েছে।\r\n\r\nসিঅ্যান্ডএফ অ্যাসোসিয়েশন একটি বেসরকারি প্রতিষ্ঠান। কিন্তু এ কার্যালয় কাস্টমস বিভাগের সরকারি জমিতে অবস্থিত। বন্দরের কার্যক্রম সুষ্ঠুভাবে পরিচালনার স্বার্থে বন্দর এলাকায় তাদের কার্যালয় রয়েছে।\r\n\r\nকিন্তু কাস্টমস কর্তৃপক্ষ ইতিপূর্বে অ্যাসোসিয়েশনের কার্যালয় সরিয়ে নেয়ার জন্য একাধিকার বলা হলেও সরানো হয়নি। বর্তমানে দুটি কমিটি সৃষ্টি হওয়ায় অপ্রীতিকর ঘটনা ঘটতে পারে। বন্দর এলাকায় কোনো অপ্রীতিকর ঘটনার দায়দায়িত্ব কাস্টমস কর্তৃপক্ষ নেবে না। ফলে অ্যাসোসিয়েশন কার্যালয়ে অতিরিক্ত একটি তালা দেয়া হয়েছে।\r\n\r\nএর আগে গেল বছরের ৩ ডিসেম্বর গঠিত কমিটির সাধারণ সম্পাদক হারুন অর রশিদ জানান, চাঁপাইনবাবগঞ্জ-১ (শিবগঞ্জ) আসনের সংসদ সদস্য ডা. সামিল উদ্দিন আহমেদ শিমুলের অনুমোদন নিয়ে আগের কমিটি গঠিত হয়েছে। জেলা আওয়ামী লীগের সভাপতি ও জেলা পরিষদের চেয়ারম্যান মঈনুদ্দীন মণ্ডল ও শিবগঞ্জ উপজেলা পরিষদের চেয়ারম্যান সৈয়দ নজরুল ইসলাম পরের কমিটিকে সুপারিশ করেছেন।\r\n\r\nদুটি কমিটি গঠন নিয়ে ক্ষমতাসীন দলের মধ্যে দ্বন্দ্ব দেখা দিয়েছে। নবগঠিত কমিটির একজন সদস্য বলেন, আগের কমিটির অনিয়ম ও দুর্নীতির প্রতিবাদে অ্যাসোসিয়েশনের সাধারণ সদস্যদের দাবির পরিপ্রেক্ষিতে এ নতুন কমিটি গঠিত হয়েছে।\r\n\r\nদুটি কমিটির কারণে উদ্বুদ্ধ জটিলতার সৃষ্টির কারণে কাস্টমস অফিস এলাকায় কর্তৃপক্ষ কার্যালয়ে তালা দিয়েছে। তবে এ পরিস্থিতি নিরসনে সমঝোতার চেষ্টা চলছে।\r\n\r\nঅন্যদিকে আগের কমিটির সাধারণ সম্পাদক হারুন অর রশিদ তাদের বিরুদ্ধে অনিয়ম, দুর্নীতির অভিযোগ অস্বীকার করে জানান, বন্দরে বাণিজ্যিক ক্ষেত্রে বিশৃঙ্খলা সৃষ্টির উদ্দেশ্যেই নতুন কমিটি গঠন করা হয়েছে।\r\n\r\nএ কমিটির অনেকেরই লাইসেন্স নেই। তবে উদ্বুদ্ধ পরিস্থিতি সমাধানের একটি মাত্র পথ সুষ্ঠু নির্বাচন। নির্বাচিত কমিটি দায়িত্ব গ্রহণ করলে একদিকে সোনামসজিদ স্থলবন্দরে বাণিজ্যিক ক্ষেত্রে গতিশীলতা ফিরে আসবে।\r\n\r\nঅন্যদিকে অ্যাসোসিয়েশনের ভেতরে কোনো ধরনের দ্বন্দ্ব থাকবে না বলে অধিকাংশ ব্যবসায়ী অভিমত ব্যক্ত করেছেন।\r\n\r\nসিঅ্যান্ডএফ এজেন্ট অ্যাসোসিয়েশনের বিবদমান সমস্যা নিয়ে স্থানীয় সংসদ সদস্য ডা. সামিল উদ্দিন আহমেদ শিমুল বলেন, পদাধিকার বলে আমি বন্দরের উপদেষ্টা। সুষ্ঠুভাবে বন্দর পরিচালনার স্বার্থে আমি একটি কমিটির অনুমোদন দিয়েছি। এ অধিকার আমার রয়েছে।\r\n\r\nএ কমিটির নেতৃবৃন্দের গ্রহণযোগ্যতা রয়েছে সাধারণ সদস্যদের কাছে। এ ছাড়া এ কমিটির শ্রম অধিদফতরের অনুমোদনও রয়েছে। নতুন যে কমিটি গঠিত হয়েছে তা সুপারিশ করার বৈধতা নেই বলে উল্লেখ করেন তিনি।\r\n\r\nশিবগঞ্জ উপজেলা পরিষদের চেয়ারম্যান সৈয়দ নজরুল ইসলাম জানান, নবগঠিত কমিটির তালিকায় আমি কোনো সুপারিশ করিনি। সরকারি বিধি মোতাবেক স্থলবন্দর সিঅ্যান্ডএফ এজেন্ট অ্যাসোসিয়েশন পরিচালিত হোক এ কথা লিখেছি। উদ্বুদ্ধ পরিস্থিতি নিরসনের জন্য নির্বাচনের মাধ্যমে কমিটি হওয়া উচিত।\r\n\r\nউল্লেখ্য, গত ২০০৯ সালে আওয়ামী লীগ ক্ষমতায় আসার পর তৎকালীন আওয়ামী লীগের সংসদ সদস্য ও সাবেক বিদ্যুৎ প্রতিমন্ত্রী ব্রিগেডিয়ার জেনারেল (অবসরপ্রাপ্ত) আলহাজ এনামুল হকের হস্তক্ষেপে তৎকালীন নির্বাচিত কমিটি ভেঙে তার অনুমোদিত কমিটি গঠন করেন।\r\n\r\nসেই সময় থেকে এখন পর্যন্ত কোনো নির্বাচিত কমিটি না করে আগের মতোই অনির্বাচিত কমিটি দিয়ে অ্যাসোসিয়েশনের কার্যক্রম পরিচালনা হয়ে আসছে। এর পর আওয়ামী লীগের সাবেক সংসদ সদস্য গোলাম রাব্বানী ও বর্তমান সংসদ সদস্য ডা. সামিল উদ্দিন আহমেদ শিমুলের নেতৃত্বে অনির্বাচিত কমিটি বন্দর পরিচালনা করে আসছে।', '2020-03-01', 'published', '2020-02-29 22:26:06', '2020-03-30 15:18:25', 'images/upload/post/eyJpdiI6Ijd2blpwS2FcL3RidzJBWVVGYnpZdnBnPT0iLCJ2YWx1ZSI6ImtMSGxycGxVcTY2eTJKOUpYbTZxQXpTUU9FMU9hdE1FYng3cnZtSEdqR0E9IiwibWFjIjoiZDY2ZDc5Mzk2ZDA3NTA5M2RiZjFiNDFlMzk2YjIyMjQyMmRhNmE1Yjk1NzA4NzkzNTcwOWIzZTJhNzUwNjFhMyJ9.jpg', 0, 2),
(14, 7, 5, '‘নিজের বাবার সামনে কীভাবে এমন পোশাক পর, লজ্জা করে না’', 'খোলামেলা পোশাক পরে বাবা অনিল কাপুরের সঙ্গে ছবি তোলায় ব্যাপক সমালোচনার শিকার হয়েছেন বলিউড অভিনেত্রী সোনম কাপুর।\r\n\r\n\r\nভারতীয় গণমাধ্যম সূত্রে জানা গেছে, আদিত্য রায় কাপুর ও দিশা পাটানি অভিনীত ছবি ‘মালাং’ ছবির প্রিমিয়ার শো অনুষ্ঠিত হয় বৃহস্পতিবার।\r\n\r\nসেখানে উপস্থিত ছিলেন বলিউডের অনেক তারকাই।\r\n\r\nএদিন বাবা অনিল কাপুরের সঙ্গে উপস্থিত হয়েছিলেন সোনম কাপুর। আর সেই অনুষ্ঠানেই বাবার সামনে খোলামেলা কাপড় পরে ক্যামেরার সামনে পোজ দেন তিনি। আর তাতেই ট্রলের শিকার হন এই অভিনেত্রী।\r\n\r\nবাবার সামনে মেয়ের এমন পোশাক পরায় নেটিজেনরা তার সমালোচনা করেছেন।\r\n\r\nকেউ কেউ সোনমের কাণ্ডজ্ঞান নিয়েও প্রশ্ন তুলেছেন। আবার একজন বলেছেন, বাবার পাশে এভাবে দাঁড়াতে লজ্জা লাগছে না?\r\n\r\nআরেকজন লিখেছেন, নিজের বাবার সামনে কীভাবে এমন পোশাক পরে যাও তোমরা? লজ্জা করে না?\r\n\r\nবলিউড তারকাদের ট্রলের শিকার হওয়ার ঘটনা নতুন কিছু নয়। সম্প্রতি গ্র্যামি অ্যাওয়ার্ড অনুষ্ঠানে পোশাক নিয়ে তুমুল বিতর্কের মুখে পড়েছিলেন প্রিয়াঙ্কা চোপড়া।', '2020-03-01', 'published', '2020-02-29 22:28:09', '2020-11-12 04:03:08', 'images/upload/post/eyJpdiI6IjlOakllaXBHcDRRWjY4S0NQZjNhWXc9PSIsInZhbHVlIjoiZStXeFZLVmo2b0Y2cVwvVzJUVmt6RG80OXRpNGVDVE9EM2xzamJIbGFaOGs9IiwibWFjIjoiMDllZmU5YTEwOGM1M2I3NDI3N2M2ZThkNWZjYmYwMWM3ZTE3MDcyY2IzNmY5MGYyMWZlNTdhMjk2ZjdiM2Q1MyJ9.jpg', 0, 3),
(15, 7, 6, 'দ্বিতীয় বিয়ে করলেন সংগীত পরিচালক শওকত আলী ইমন', 'সংগীত পরিচালক, সুরকার, গীতিকার ও কণ্ঠশিল্পী শওকত আলী ইমন বিয়ে করেছেন। এটি তার দ্বিতীয় বিয়ে।\r\n\r\nপাত্রী হৃদিতা রেজা একটি বেসরকারি চ্যানেলে সংবাদ পাঠিকা হিসেবে কর্মরত।\r\n\r\nবৃহস্পতিবার রাজধানীর বাংলামোটরের একটি রেস্তোরাঁয় তাদের বিয়ের আনুষ্ঠানিকতা সম্পন্ন হয়। বিয়েতে দুই পরিবার সদস্যরা উপস্থিত ছিলেন।\r\n\r\nআগামী ঈদের পর বিয়ের সংবর্ধনা অনুষ্ঠান হবে বলে জানিয়েছেন শওকত আলী ইমন। তিনি জানন, বিয়ের আগে আমাদের একে অপরের সঙ্গে কোনো পরিচয় ছিল না। পারিবারিক দেখাশোনার পরই বিয়ে হয় আমাদের।\r\n\r\nহৃদিতার প্রথম বিয়ে হলেও শওকত আলী ইমনের দ্বিতীয় বিয়ে। ১৯৯৫ সালের ১৫ জুলাই অভিনেত্রী ও মডেল বিজরী বরকতুল্লাহ ও শওকত আলী ইমনের বিয়ে হয়েছিল।\r\n\r\n২০১২ সালের শেষের দিকে তাদের বিচ্ছেদ হয়। ওই সংসারে এক মেয়ে রয়েছে।', '2020-03-01', 'published', '2020-02-29 22:29:19', '2020-04-29 23:27:03', 'images/upload/post/eyJpdiI6Im42Y3pWUFZzZXFKS2dNWnFZeVRJVVE9PSIsInZhbHVlIjoiaXo4eWhQdktKSnNkR1BnekVlem15VFYxWkR4NXBVdUtIdUdFdWt4RjJMbz0iLCJtYWMiOiI0NGJlZTZlZmJhODg5ODZiNDRmOTBlODVmN2IzOWExNDM1ODcwOWRmNjU0NjcyMTgzNTVjMDgxNmI5ZWJmN2Q5In0=.jpg', 0, 4),
(16, 5, 5, 'করোনাভাইরাসে যুক্তরাষ্ট্রে প্রথম মৃত্যু', 'করোনাভাইরাসে মার্কিন যুক্তরাষ্ট্রের ওয়াশিংটনে এক ব্যক্তি মারা গেছেন। বিশ্বব্যাপী আতঙ্ক সৃষ্টিকারী এই ভাইরাসে দেশটিতে এটিই প্রথম মৃত্যুর ঘটনা।\r\n\r\nওয়াশিংটন রাজ্যের গভর্নর জে ইনস্লি শনিবার এক বিবৃতিতে জানিয়েছেন, করোনাভাইরাসে মৃত্যুর ঘটনা খুবই দুঃখজনক। নাগরিকদের সুস্থ ও নিরাপদ রাখতে আমাদের যথেষ্ট প্রস্তুতি রয়েছে। খবর সিএনএনের।\r\n\r\nএর আগে গত ৬ ফেব্রুয়ারি চীনের হুবেইপ্রদেশের উহান শহরে করোনাভাইরাসে আক্রান্ত হয়ে এক মার্কিন নাগরিকের মৃত্যু হয়েছে।\r\n\r\nযুক্তরাষ্ট্রে এ পর্যন্ত ৬৭ জন করোনাভাইরাসে আক্রান্ত হয়েছেন। ভাইরাসের দ্রুত বিস্তার ঠেকাতে ক্যালিফোর্নিয়া অঙ্গরাজ্যের সান ফ্রানসিসকেসহ বেশ কয়েকটি শহরে ইতোমধ্যে স্বাস্থ্যগত জরুরি অবস্থা ঘোষণা করা হয়েছে।\r\n\r\nযুক্তরাষ্ট্রের স্বাস্থ্য কর্মকর্তারা বলছেন, কয়েকজনের শরীরে সম্প্রতি করোনাভাইরাসের অস্তিত্ব ধরা পড়েছে। এর মধ্যে তারা করোনাকবলিত দেশগুলোতে বেড়াতে যাননি, দেশেই ছিলেন।\r\n\r\nতা সত্ত্বেও কীভাবে সংক্রমণ ছড়াল তা নিয়ে তারা চিন্তিত। এ ছাড়া দক্ষিণ কোরিয়া থেকে ঘুরে আসা এক নারীও করোনাভাইরাসে আক্রান্ত। তারা সবাই হোম আইসোলেশনে রয়েছেন। করোনাভাইরাসে বিধ্বস্ত শহরগুলোতে ভ্রমণের ক্ষেত্রেও নিষেধাজ্ঞা জারি হয়েছে।\r\n\r\nগত বছরের ডিসেম্বরের শেষ দিকে চীনে ভাইরাসটি প্রথম দেখা দেয়। বিশ্বের ৫৬ দেশের ৮৫ হাজার মানুষ এই ভাইরাসে আক্রান্ত হয়েছেন। এর মধ্যে প্রায় ২৯০০ জনের মৃত্যু হয়েছে। নিহতের বেশিরভাগ চীন দেশের নাগরিক।', '2020-03-01', 'published', '2020-02-29 22:31:47', '2020-02-29 22:31:47', 'images/upload/post/eyJpdiI6IlVHQ1NRSUEwV3VZYkkzNzlaZVpXblE9PSIsInZhbHVlIjoid1hjQ1NZSVwvbGVlMVwvaWRhSTJmYzA1c1NmeHRKZHkyTUpvb3kzK0NvTnNFPSIsIm1hYyI6ImZmODQ5MzZkYjI0ZDRjYjJlN2RmZWViNWIwNmM3ZTI4MDU0NTM3OTUwMDI2NGJiYTY0NjllMWQ3Yjk4NjkxZjAifQ==.jpg', 0, 0),
(17, 5, 5, 'শান্তি চুক্তি সই করতে কাতারে মার্কিন পররাষ্ট্রমন্ত্রী', 'তালেবানদের সঙ্গে শান্তি চুক্তি সই করতে কাতারের রাজধানী দোহায় পৌঁছেছেন পররাষ্ট্রমন্ত্রী মাইক পম্পেও। আফগানিস্তান থেকে মার্কিন সেনা প্রত্যাহার করে নেয়ার বিষয়ে তালেবানের সঙ্গে চুক্তিতে স্বাক্ষর করবেন তিনি।\r\n\r\n\r\nশনিবার দোহায় তালেবান যোদ্ধাদের সঙ্গে একটি বৈঠকে এ চুক্তি স্বাক্ষর অনুষ্ঠিত হবে। বৈঠকে অংশ নিতে ৩১ সদস্যের তালেবানের একটি প্রতিনিধি দল আগেই কাতার পৌঁছান।\r\n\r\nতালেবানের মুখপাত্র জবিউল্লাহ মুজাহিদ বার্তাসংস্থা রয়টার্সকে জানিয়েছেন, আমরা আশা করি দুই পক্ষের মধ্যে আলোচনা এবং শান্তি চুক্তি সই হওয়ার সময় যুক্তরাষ্ট্র তার প্রতিশ্রুতির প্রতি অনড় থাকবে।\r\n\r\nতিনি বলেন, জাতির এ খুশির দিনে আজকে তালেবানের সব সদস্যকে যেকোনো ধরনের হামলা থেকে বিরত থাকার জন্য নির্দেশ দেয়া হয়েছে।\r\n\r\nতবে বিদেশি জঙ্গিবিমানগুলো ভয় ও আতঙ্ক সৃষ্টি করতে এখনো তাদের নিয়ন্ত্রিত এলাকায় টহল দিচ্ছে বলে তালেবান অভিযোগ করেছেন তালেবান মুখপাত্র।\r\n\r\nচুক্তির আওতায় যুদ্ধবিধ্বস্ত আফগানিস্তান থেকে হাজার হাজার মার্কিন সেনা প্রত্যাহারের পাশাপাশি দেশটিতে স্থায়ী যুদ্ধবিরতি পালনের কথা বলা হয়েছে।\r\n\r\nএর আগে মার্কিন পররাষ্ট্রমন্ত্রী পম্পেও এক বিবৃতিতে বলেছিলেন, আমরা তালেবানের সঙ্গে আফগানিস্তানজুড়ে সহিংসতা কমানোর ব্যাপারে একটি সমঝোতায় পৌঁছেছি।\r\n\r\nএই সমঝোতা সফলভাবে বাস্তবায়ন হলে যুক্তরাষ্ট্র-তালেবান (শান্তি) চুক্তি নিয়ে সামনে আগানো যাবে বলে আশা করা যায়। আমরা ২৯ ফেব্রুয়ারি এ চুক্তি সই করার প্রস্তুতি নিচ্ছি।', '2020-03-01', 'published', '2020-02-29 22:32:56', '2020-03-31 10:08:47', 'images/upload/post/eyJpdiI6ImFGV2VjcFRGbjJzdERheFRFb0piUVE9PSIsInZhbHVlIjoiV2lVS1JiWnJCTU9lZjdpUzl4aittOHNWYmhsRExzQjBPYm5cL0FRR3FZckE9IiwibWFjIjoiZTU2MGQ2OTYwNDA2YmI3NzRhYWRjNjA1OTY0OTQyODc3NGY1Njg5ZjZlNjNlYjFlZmQzMWIyNjNiMWU5ZDBmOSJ9.jpg', 0, 1),
(18, 2, 3, 'রেসিপি জানেন রামোস', 'নিজেদের পায়ে বল ধরে রাখাটাই বার্সেলোনার মূল শক্তি বলে মনে করেন সের্গিও রামোস। লা লিগা চ্যাম্পিয়নদের চাপে রেখে তাদের পা থেকে বল কেড়ে নিতে পারলেই জয়ের রাস্তা খুলবে বলে মনে করেন রিয়াল মাদ্রিদ অধিনায়ক।\r\n\r\n\r\nআজ বার্নাব্যুতে মৌসুমের দ্বিতীয় এল ক্লাসিকোর আগে রামোস জানালেন বার্সা-বধের রেসিপি তাদের জানা আছে। বার্সেলোনাকে হারাতে পারলে ফের লা লিগার শীর্ষে ফিরবে রিয়াল। আর হারলে ফিকে হয়ে যাবে শিরোপার সম্ভাবনা।\r\n\r\nচিরপ্রতিদ্বন্দ্বীদের বিপক্ষে ম্যাচটির গুরুত্ব ভালোভাবেই জানেন রামোস, ‘আমরা তাদের বিপক্ষে অনেকবার খেলেছি। তাই আমরা জানি তাদের কিভাবে সংকটে ফেলা যায়। বার্সেলোনা এমন একটা দল যারা নিজেদের পায়ে অনেক বেশি বল রাখে। তারা সবচেয়ে বেশি অস্বস্তিতে ভোগে যখন আপনি তাদের পা থেকে বল কেড়ে নেয়ার চেষ্টা করেন। শুধু রিয়াল মাদ্রিদ নয়, এটা সবাই জানে। তাদের ওপর অনেক বেশি চাপ ধরে রাখাটা ঝুঁকিপূর্ণ। কিন্তু আমি মনে করি, এটাই চাবিকাঠি। মাঠে তাদের ওপর অনেক বেশি চাপ ধরে রাখা এবং তাদের পা থেকে বল কেড়ে নেয়ার চেষ্টা করা।’', '2020-03-01', 'published', '2020-02-29 22:33:49', '2020-04-08 21:04:13', 'images/upload/post/eyJpdiI6ImVRaHdGeDBLMld3bStoeVwvSDFzbVB3PT0iLCJ2YWx1ZSI6Im9RTE94REdIZnBoRWdlUE9FSUNnZE1taVRZQndvdGRHS3JqaUdDY3k5ZzQ9IiwibWFjIjoiOGE0NGJhOTNhNTA2NDhkOGIxMDVkYWM1NzBkOTNjMTdlY2JmM2EyNjE0ZTJjYWNjOWFmMjA0YzlhYjc5YjhlNyJ9.jpg', 0, 31),
(19, 2, 6, 'খোঁড়াদের এল ক্লাসিকো!', 'গত মৌসুমে দ্বিতীয় এল ক্লাসিকোর আগে রিয়াল মাদ্রিদের চেয়ে নয় পয়েন্টে এগিয়েছিল বার্সেলোনা। তার আগের মৌসুমে ব্যবধান ছিল ১৫ পয়েন্ট। এবার ছবিটা একটু অন্যরকম। লা লিগায় মৌসুমের সবচেয়ে গুরুত্বপূর্ণ ম্যাচটি আজ। রিয়ালের ডেরা বার্নাব্যুতে বাংলাদেশ সময় রাত ২টায় মুখোমুখি হবে দুই চিরপ্রতিদ্বন্দ্বী। লিগের দ্বিতীয় এল ক্লাসিকোর আগে ২৫ ম্যাচে ৫৫ পয়েন্ট নিয়ে শীর্ষে আছে বার্সেলোনা।\r\n\r\nসমান ম্যাচে দ্বিতীয় স্থানে থাকা রিয়ালের পয়েন্ট ৫৩। ব্যবধান মাত্র দুই পয়েন্ট হওয়ায় এই ম্যাচটি গড়ে দিতে পারে লা লিগার শিরোপাভাগ্য। ঘরের মাঠে হারলে শিরোপার দৌড় থেকে প্রায় ছিটকে যাবে রিয়াল। অন্যদিকে রিয়াল জিতলে শীর্ষস্থান হারাবে বার্সা। এমন মহাগুরুত্বপূর্ণ এল ক্লাসিকো সাম্প্রতিক সময়ে আর দেখা যায়নি। অথচ অগ্নিগর্ভ এই লড়াইকে কি না বলা হচ্ছে খোঁড়াদের ম্যাচ!\r\n\r\nএ মৌসুমে দুই স্প্যানিশ জায়ান্টই খুব বাজে সময়ের মধ্য দিয়ে যাচ্ছে। একইদিনে তারা বিদায় নিয়েছে কোপা দেল রে থেকে। চ্যাম্পিয়ন্স লিগের শেষ ষোলোর প্রথম লেগে নাপোলির মাঠে হারতে হারতে ড্র করেছে বার্সেলোনা। মাঠের বাইরেও ঘোর সংকটে আছে কাতালানরা। ক্লাব কর্তৃপক্ষের সঙ্গে ঠাণ্ডা লড়াই চলছে খেলোয়াড়দের। দল লিগের শীর্ষে থাকা অবস্থায় গত মাসে আগের কোচ আর্নেস্তো ভালভার্দেকে ছাঁটাই করেছে বার্সা।\r\n\r\nচোটের মিছিল দীর্ঘ থেকে দীর্ঘতর হচ্ছে। কিন্তু বার্সেলোনার এই সংকটের সুযোগ নিতে পারেনি রিয়াল। তাদের অবস্থা আরও শোচনীয়। ঘরের মাঠ শেষ ষোলোর প্রথম লেগে ম্যানসিটির কাছে ২-১ গোলে হেরে চ্যাম্পিয়ন্স লিগ থেকে বিদায়ের মুখে জিনেদিন জিদানের দল। সবমিলিয়ে শেষ তিন ম্যাচের দুটিতে হেরেছে রিয়াল, অন্যটিতে ড্র করেছে। রিয়ালের ব্যর্থতার কারণেই গত সপ্তাহে তাদের হটিয়ে লিগের শীর্ষে ফিরেছে বার্সা।\r\n\r\nকাতালানদের জন্য স্বস্তির ব্যাপার হল, যে কোনো সংকট থেকে উদ্ধার করার জন্য একজন লিওনেল মেসি আছে তাদের। কিন্তু রিয়াল এখনও পারেনি ক্রিশ্চিয়ানো রোনাল্ডোর শূন্যতা পূরণ করতে। মৌসুমের শুরুতে করিম বেনজেমা কাজ চালিয়ে নিচ্ছিলেন। মিডফিল্ডাররাও জোগান দিচ্ছিলেন কিছু গোল। কিন্তু মৌসুমের শেষভাগে এসে সবাই যেন একসঙ্গে খেই হারিয়ে ফেলেছেন। লিগে টানা চার ম্যাচে গোলহীন বেনজেমা। ফর্মে নেই বেল, রদ্রিগেজরাও। চোট নিয়ে মাঠের বাইরে এডেন হ্যাজার্ড।\r\n\r\nবার্সেলোনাও চোটের থাবায় হারিয়েছে লুইস সুয়ারেজ ও উসমান ডেম্বেলেকে। তবে বার্সার আক্রমণভাগে অধিনায়ক মেসির সঙ্গে আছেন আঁতোয়া গ্রিজমান ও আনসু ফাতি। প্রতিপক্ষের মাঠে খেলা হলেও বার্সাকে তাই একটু এগিয়ে রাখছেন ফুটবলবোদ্ধারা। পরিসখ্যানও সেটাই বলছে। লিগে গত চার মৌসুমেই রিয়ালের ডেরা থেকেই জয় নিয়ে ফিরেছে বার্সেলোনা।\r\n\r\nসব প্রতিযোগিতা মিলিয়ে রিয়ালের বিপক্ষে টানা ছয় ম্যাচে অপরাজিত বার্সা। তবে অতীতে বহুবার এমন কোণঠাসা পরিস্থিতিতে ঘুরে দাঁড়িয়ে বাজিমাত করেছে রিয়াল। শেষ পর্যন্ত অবশ্য ম্যাচের ফল যাই হোক, চিত্তাকর্ষক ফুটবলের আশা ছেড়ে দিয়েছেন রিয়ালের সাবেক ফরোয়ার্ড ও কোচ হোর্হে ভালদানো, ‘এবারের ক্লাসিকো খোঁড়াদের লড়াই। দুই দলই খুব বাজে সময়ের মধ্য দিয়ে যাচ্ছে। নিজেদের কৃতিত্বে লিগের শীর্ষস্থান ফিরে পায়নি বার্সেলোনা। রিয়ালের বাজে ফর্মই শীর্ষে ফিরিয়েছে বার্সাকে।’\r\n\r\nন্যুক্যাম্পে মৌসুমের প্রথম এল ক্লাসিকো গোলশূন্য ড্র হয়েছিল। তবে তুলনামূলক ভালো খেলেছিল রিয়াল। এবার নতুন কোচ কিকে সেতিয়েনের অধীনে শত্রুর ডেরায় যাচ্ছেন মেসিরা। প্রবল প্রতিপক্ষ হলেও মহারণের আগে মেসির প্রতি শ্রদ্ধাই ঝরল রিয়াল অধিনায়ক সের্গিও রামোসের কণ্ঠে, ‘তার প্রতি আমার অনেক শ্রদ্ধা। ইতিহাসের অন্যতম সেরা খেলোয়াড় মেসি। আশা করছি, রোববার দিনটা তার হবে না।’\r\n\r\nএকইসঙ্গে বার্সা-বধের টোটকাও জানিয়ে দিলেন রামোস, ‘আমরা জানি কিভাবে তাদের সমস্যায় ফেলতে হয়। বার্সেলোনা সবসময় বলের দখল রাখতে চায়। এটা যদি ওদের করতে না দেয়া হয়, তখন অস্বস্তিতে পড়ে যায়। আমাদের নিশ্চিত করতে হবে, বার্সা যেন নিজেদের মতো খেলতে না পারে। সেজন্য প্রথম মিনিট থেকেই মনোযোগ ধরে রাখতে হবে।’', '2020-03-01', 'published', '2020-02-29 22:36:43', '2020-04-08 21:04:20', 'images/upload/post/eyJpdiI6ImtCWWZcLzA5WEM1VExiTng4RkVmSWJBPT0iLCJ2YWx1ZSI6IlI5Z1NYUGZMeDFsQjhwNURTMDd2YnVJTlpia1lvK3RlVWc0em00ZXRlYzA9IiwibWFjIjoiNjRhMDgwNzEwYzY0MmJiNWYwNDBhMGI3MzMwODIzNWIwNTM3ZTE3MTNiOWRjMzgzZmQ5MzYwOTA1MWQyZDc5NCJ9.jpg', 0, 2),
(20, 5, 5, 'ভারতে মসজিদে আগুন দেয়ায় পাক প্রেসিডেন্টের নিন্দা', 'ভারতে বিতর্কিত নাগরিকত্ব সংশোধনী আইন নিয়ে সমর্থক ও বিরোধীদের সংঘর্ষের পর মঙ্গলবার নয়া দিল্লির অশোক নগরে একটি মসজিদে আগুন দেয়ার ঘটনায় নিন্দা জানিয়েছেন পাকিস্তানের প্রেসিডেন্ট আরিফ আলভি।\r\n\r\n\r\nবুধবার এ খবর জানিয়েছে পাক গণমাধ্যম ডন অনলাইন। দেশটিতে নাগরিকত্ব সংধোশনী আইন পাসের পর থেকেই এর বিরোধিতা করে আসছে বিশেষ করে মুসলিমরা। এর অংশহিসেবে রোববারও আন্দোলন চলতে থাকে। তবে মার্কিন প্রেসিডেন্ট ডোনাল্ড ট্রাম্পের দুইদিনের ভারত সফরকে কেন্দ্র করে সোমবার ও মঙ্গলবার সংঘর্ষ ছড়িয়ে পড়ে। বিশেষ করে মঙ্গলবার উত্তর-পশ্চিম নয়া দিল্লিতে হিন্দু ও মুসলিমদের মধ্যে রক্তক্ষয়ী সংঘর্ষ ছড়িয়ে পড়ে। আজ বুধবার পর্যন্ত এ সংঘর্ষে নিহত বেড়ে ২৩ জনে দাঁড়িয়েছে। এ ছাড়া আহত হয়েছে ২ শতাধিক।\r\n\r\nমঙ্গলবার সামাজিক যোগাযোগ মাধ্যমে ছড়িয়ে পড়া একটি ভিডিওতে দেখা যায় মসজিদ ভাঙচুর চালাচ্ছে সংঘর্ষে অংশ নেয়া হিন্দুরা। মসজিদের মিনারে উঠে মাইক খুলে ফেলছে। মিনারটির উপরে উঠে হনুমান ও ভারতীয় পতাকা লাগাচ্ছে। মোবাইলে ধারণ করা ওই ভিডিওতে শোনা যায় জয় শ্রি রাম ও হিন্দুকা হিন্দুস্তান। ছড়িয়ে পড়া ওই ভিডিওটি সংবাদ সংস্থা এএফপি ভেরিফায়েড করেছে বলে প্রতিবেদনে প্রকাশ করা হয়।\r\n\r\nতবে বুধবার এএফটির প্রতিবেদনে বলা হয়, এ দিন সকালে মানুষ দেখেছে মসজিদটির অভ্যন্তরীণ অংশকে কালো করে ফেলা হয়েছে।\r\n\r\nমঙ্গলবার গভীর রাতে করা প্রেসিডেন্ট আরিফ আলভির টুইটে বলা হয়, আরেকটি অপমানজনক কাজের রূপ দেয়া। একটি মসজিদ ভাঙচুর করা!এটি মনে হয় মুসলিমদের বাবরি মসজিদের ভাঙচুরের কথা মনে করিয়ে দেয়া।\r\n\r\nতিনি ওই টুইট বার্তায় লেখেন, আমি মনে করি যে, এই ধরনের বর্বরোচিত কাজের বিরুদ্ধে ভারতের অভ্যন্তরে ধর্মনিরপেক্ষ শক্তির উত্থান হওয়া উচিত।', '2020-03-01', 'published', '2020-02-29 23:30:16', '2020-03-31 09:31:14', 'images/upload/post/eyJpdiI6IldFQitoMmIrQkYzUXRcL3d5QVNGODBRPT0iLCJ2YWx1ZSI6IldhXC80OTBOQVdLSFNEZ0xtYllpcEltc2dlNDZJOWdFckFaeURKaUhZcWRnPSIsIm1hYyI6ImZjZmNiYzY0NmNkZWQ0YjY5ZGEzNTBmY2ZiOWYwNjQ0NmQzNjI1YjRhZDViOWUyMDMxNjRiMDk1YjYxZTlmNmMifQ==.jpg', 0, 5),
(21, 5, 6, 'মুহিউদ্দীন আমার সঙ্গে বিশ্বাসঘাতকতা করেছেন: মাহাথির', 'আধুনিক মালয়েশিয়ার রূপকার ড. মাহাথির মোহাম্মদকে টেক্কা দিয়ে প্রধানমন্ত্রী হয়েছেন দেশটির প্রভাবশালী নেতা মুহিউদ্দীন ইয়াসিন।\r\n\r\nগতকাল শনিবার তাকে প্রধানমন্ত্রী হিসেবে নিয়োগ দেন দেশটির রাজা সুলতান আবদুল্লাহ সুলতান আহমাদ শাহ।\r\n\r\nরোববার স্থানীয় সময় সকাল সাড়ে ১০টায় দেশটির অষ্টম প্রধানমন্ত্রী হিসেবে শপথ নেবেন মুহিউদ্দীন।\r\n\r\nমুহিউদ্দীনের শপথের আগেই আজ সকালে ইয়াইয়াসান আল বুখারিতে সংবাদ সম্মেলন করেন মাহাথির। সেখানে তিনি মনের ক্ষোভ উগরে দিয়েছেন।\r\n\r\nমাহাথির বলেছেন, আমার সঙ্গে বিশ্বাসঘাতকতা করা হয়েছে। বিশেষ করে মুহিউদ্দীনের পক্ষ থেকে এটি বেশি করা হয়েছে।\r\n\r\nসংবাদ সম্মেলনে মাহাথির আরও বলেন, মুহিউদ্দীন ইয়াসিন দীর্ঘদিন তার এই লক্ষ্য নিয়ে কাজ করছিলেন। এখন তিনি সেই বিশ্বাসঘাতকতায় সফল হয়েছেন।\r\n\r\nএক প্রশ্নের জবাবে মাহাথির বলেন, আজমিনের নিজস্ব এজেন্ডা ছিল। এই পুরো এজেন্ডা ছিল তার। আর সে মুহিউদ্দীনের সঙ্গে ঘনিষ্ঠভাবে কাজ করেছে তা নিয়ে।\r\n\r\nপাকাতান হারাপান জোটের পরবর্তী পদক্ষেপ সম্পর্কে তিনি বলেন, জরুরি ভিত্তিতে পাকাতান জোট পার্লামেন্টারি অধিবেশন ডাকবে। আমরা দেখব (এমপিদের পক্ষ থেকে) কার কত সংখ্যাগরিষ্ঠ সমর্থন আছে। এখন থেকে বিরোধী অবস্থানে যাবে পাকাতান জোট।\r\n\r\nপার্তি প্রিবুমি বারসাতু মালয়েশিয়ার ভবিষ্যৎ সম্পর্কে মাহাথির বলেন, এটি এখন নির্ভর করে দলীয় সিদ্ধান্তের ওপর। তারা আমাকে চেয়ারম্যান পদ থেকে বহিষ্কার করবে নাকি রাখবে সে সিদ্ধান্ত দল নেবে। বারসাতুর সংখ্যাগরিষ্ঠরা আমাকে চেয়ারম্যান হিসেবে প্রত্যাখ্যান করেছেন। ফলে কিছু একটা তো হতে যাচ্ছে। তারা এখন আনুষ্ঠানিকভাবে আমাকে বহিষ্কার করতে পারেন।\r\n\r\nপ্রধানমন্ত্রী পদত্যাগ করা নিয়ে তিনি অনুশোচনা বোধ করেন কিনা? এ প্রশ্নের জবাবে মাহাথির বলেন, তার সামনে এ ছাড়া আর কোনো বিকল্প ছিল না।\r\n\r\nতিনি বলেন, আমি দেখেছি– উমনো এন-ব্লককে নিয়ে বারসাতু জোট গঠন করতে যাচ্ছে। তখন (পদত্যাগ করা ছাড়া) আমার সামনে আর কোনো বিকল্প ছিল না। এর অর্থ হলো– যাদের বিরুদ্ধে আমি লড়াই করেছি, তাদের মেনে নিতে হবে। এটি করতে আমি প্রস্তুত নই। তাই মুহিউদ্দীন এটি করতে প্রস্তুত ছিলেন।\r\n\r\nমাহাথিরের সরকারে স্বরাষ্ট্রমন্ত্রী ছিলেন মুহিউদ্দীন ইয়াসিন। প্রধানমন্ত্রী প্রার্থী হিসেবে মুহিউদ্দীনের নাম এর আগেই ঘোষণা করেছিল মাহাথিরের দল মালয়েশিয়ার ইউনাইটেড আদিবাসী পার্টি (বারসাতু)।\r\n\r\nনিজের ক্ষমতা পাকাপোক্ত করতেই মাহাথির আচমকা পদত্যাগ করেন বলে জল্পনা-কল্পনা চলছিল দেশটির রাজনৈতিক অঙ্গনে। তার পদত্যাগের পর ৭৩ বছর বয়সী আনোয়ার ইব্রাহীমের সঙ্গে তার দলের জোটে ভাঙন ধরে। ২০১৮ সালের নির্বাচনে নাজিব রাজাককে হারিয়ে এ জোট বিশাল ব্যবধানে জয়ী হয়ে ক্ষমতায় আসে।\r\n\r\nমালয়েশিয়ার রাজনীতির মঞ্চে সপ্তাহব্যাপী নানা নাটকীয়তার পর পদত্যাগ করেন মাহাথির। ‘ফ্যাক্ট অব হোপ’ নামে আনোয়ার ইব্রাহীম যে জোট গঠন করে ২০১৮ সালের নির্বাচনে ঐতিহাসিক জয় পেয়েছিলেন, সেই জোটের ভেতরে আনোয়ার বিরোধীরা এ নাটক মঞ্চস্থ করেছে বলে অভিযোগ ওঠে।\r\n\r\nমাহাথির মোহাম্মদ পদত্যাগ করলে মালয়েশিয়ার প্রধানমন্ত্রী হওয়ার কথা ছিল আনোয়ার ইব্রাহীমের। তিনি হলেন দেশটির সাবেক উপপ্রধানমন্ত্রী। কিছু দিন ক্ষমতায় থেকে আনোয়ার ইব্রাহীমের হাতে ক্ষমতা অর্পণ করবেন ২০১৮ সালের নির্বাচনের পর এমন প্রতিশ্রুতি দিয়েই প্রধানমন্ত্রী হন মাহাথির মোহাম্মদ। কিন্তু সেই প্রতিশ্রুতি মাহাথির রাখেননি। সূত্র: অনলাইন দ্য স্টার।', '2020-03-01', 'published', '2020-02-29 23:32:27', '2020-04-28 07:08:46', 'images/upload/post/eyJpdiI6InNxMGdvSmpjMXBJSXdJdnFEMGRyWHc9PSIsInZhbHVlIjoic2IyWFJxRmRZS0NjZUlhZlMzZDhCNlM2cDVZdk1pWlFUeEVQNlZmcmczOD0iLCJtYWMiOiJjNjc1MzI5NjNjMmE0NzNjYzk5NjZkOWNjYjg3ZTdmYWRkOGRiNTEyM2FkMzI3MWM1NWQ5OGExYzBkNTZiMzRhIn0=.jpg', 0, 3),
(22, 2, 3, 'বাস্তেনের সেরা মেসি', 'লিওনেল মেসি ও ক্রিশ্চিয়ানো রোনাল্ডোর মধ্যে কে সেরা- চলমান এই বিতর্কে এবার যোগ দিলেন মার্কো ফন বাস্তেন। ডাচ কিংবদন্তির কাছে মেসিই সেরা। এমনকি সেরার বিতর্কে যারা মেসিকে রেখে রোনাল্ডোকে বেছে নেন, তারা ফুটবলই বোঝেন না বলে মনে করেন ইন্টার মিলানের সাবেক স্ট্রাইকার। মেসি ও রোনাল্ডো দু’জনকেই সর্বকালের সেরা ফুটবলারদের তালিকায় রাখা হয়।\r\n\r\n\r\nফুটবলের আঙিনায় দু’জনের দ্বৈরথ চলছে তো চলছেই। রিয়াল মাদ্রিদ ছেড়ে রোনাল্ডো জুভেন্টাসে পাড়ি জমালেও থামেননি। বার্সেলোনা অধিনায়ক মেসি ছয়টি ব্যালন ডি’অর জিতেছেন, রোনাল্ডো পাঁচটি। দু’জনের অর্জনের তালিকায় আছে ভূরিভূরি ব্যক্তিগত ও দলীয় সাফল্য।\r\n\r\nতবে ফন বাস্তেনের চোখে মেসিই সেরা, ‘ক্রিশ্চিয়ানো রোনাল্ডো অসাধারণ খেলোয়াড়; কিন্তু যারা দাবি করে সে মেসির চেয়ে ভালো, তারা ফুটবলের কিছুই বোঝে না বা এটা তারা বাজে বিশ্বাস নিয়ে বলে। মেসি অনন্য, অপূর্ব এবং তার মতো খেলোয়াড় প্রতি ৫০ বা ১০০ বছরে একজনই আসে। শৈশবেই তাকে ফুটবল জিনিয়াসের পটে রাখা হয়েছিল।’\r\n\r\nআরেকটি দারুণ মৌসুম কাটাচ্ছেন রোনাল্ডো। এরই মধ্যে জুভেন্টাসের হয়ে সব প্রতিযোগিতা মিলিয়ে ৩৫ বছর বয়সী এই ফরোয়ার্ড করেছেন ২৫ গোল। অন্যদিকে লা-লিগা চ্যাম্পিয়ন বার্সেলোনার হয়ে চলতি মৌসুমে এরই মধ্যে সব প্রতিযোগিতা মিলিয়ে ২৩ গোল করেছেন মেসি। লিগে আর্জেন্টাইন ফরোয়ার্ডের গোল ২০ ম্যাচে ১৮টি।', '2020-03-01', 'published', '2020-02-29 23:33:45', '2020-08-21 10:34:55', 'images/upload/post/eyJpdiI6IkFFakU4V2I4SEkyZHR4XC84NEZLVUN3PT0iLCJ2YWx1ZSI6ImVraGMwN3NwTUwxWWtocW1ydGFyRzFJZlhPZUZ3UnpseXljSUpFWnY0WHM9IiwibWFjIjoiZjBiZTcxOGZkMDdhMzFmZTViOWIyMGYyNWY4MTZjYjc5ZTdjMmU3Nzk0ZWM2NTExZmIwN2ZhZjYyNzg3MGI4NCJ9.jpg', 0, 5),
(23, 2, 3, '‘আমি আর আগের লিটন নেই’', 'জিম্বাবুয়ের বিপক্ষে ১০৫ বলে ১২৬ রানের ঝকঝকে ইনিংস খেলে বাংলাদেশের রেকর্ড জয়ে অগ্রণী ভূমিকা রেখেছেন লিটন কুমার দাস।\r\n\r\nখেলা শেষে ম্যাচ সেরার পুরস্কার জয়ের পর জাতীয় দলের এ ওপেনার বলেন,বিপিএলের সময় বলেছি- বিয়ের পর আমার পরিপক্বতা বেড়েছে। খেলা দেখে বোঝা যায়, ব্যাটিং দেখে হয়ত বুঝতে পেরেছেন আমি দুই বছর আগে যে লিটন ছিলাম, এখন সেই লিটন নেই।\r\n\r\nরোববার সিলেট স্টেডিয়ামে খেলা শেষে লিটন আরও বলেন, আমি আগে নিজের ব্যাটিং দেখতাম না। গত এক বছর ধরে খেলা শেষে আমি আমার ব্যাটিংয়ের রিভিউ দেখি। তখন নিজের যে ভুলগুলো চোখে পরে সেগুলো পরবর্তী ম্যাচের আগে শুধরে নেয়ার চেষ্টা করি।\r\n\r\n২০১৮ সালে দুবাইয়ে এশিয়া কাপের ম্যাচে শক্তিশালী ভারতের বিপক্ষে ১২১ রানের ইনিংস খেলেছিলেন লিটন। তার সেঞ্চুরির পরও ২২২ রানে অলআউট হয়ে যাওয়া বাংলাদেশ সেদিন হেরে যায় ৩ উইকেটে।\r\n\r\nরোববার জিম্বাবুয়ের বিপক্ষে ঘরের মাঠে সিলেটে খেলেছেন ক্যারিয়ার সেরা ১২৬ রানের ইনিংস। আন্তর্জাতিক ক্রিকেটে নিজের দুই সেঞ্চুরির মধ্যে সেরা কোনটি। এমন প্রশ্নের জবাবে লিটন বলেন, এশিয়া কাপের সেই সেঞ্চুরিটাই অবশ্যই অনেক উপরে থাকবে। ওই সেঞ্চুরি এশিয়া কাপে ছিল। আজকেরটা হয়ত এত বড় ম্যাচ না। এশিয়া কাপে বড় দলের বিপক্ষে সেঞ্চুরি করেছিলাম।', '2020-03-01', 'published', '2020-03-01 13:24:43', '2020-11-15 08:01:06', 'images/upload/post/eyJpdiI6IlhBUXB0Z2tTWW5QR21IdlpLbWJ3RHc9PSIsInZhbHVlIjoiM09pYWhRelhUeHFWaForUFhha0tOaldrSFFCYXQzMGloREtHeWpqSkRWVT0iLCJtYWMiOiJlMmE2MGYxMmYwYWUxOTBkZjhiMGU0YzY4NjE4ZWEyODExNDMwODBhOGViMDBiYTRmYjczMGU2MDk2ZTI5ZTdmIn0=.jpg', 0, 6),
(24, 1, 5, 'শহরের সুবিধা পৌঁছাবে ৮ বিভাগের ১৬ গ্রামে', 'জাতির পিতা বঙ্গবন্ধু শেখ মুজিবুর রহমানের জন্মশতবর্ষ বা মুজিববর্ষে শহরের মতো নাগরিক সুবিধা পৌঁছে দিতে প্রাথমিকভাবে দেশের ৮টি বিভাগের ১৬টি গ্রামকে বেছে নিয়েছে সরকার। ইতিমধ্যে প্রকল্প বাস্তবায়নের লক্ষ্যে পরামর্শক প্রতিষ্ঠান নিয়োগ ও ডেভেলপমেন্ট প্রজেক্ট প্রোফাইল (ডিপিপি) প্রস্তত কাজ শুরু করেছে পল্লী উন্নয়ন ও সমবায় বিভাগ।\r\n\r\n\r\nআওয়ামী লীগের নির্বাচনী ইশতেহারে বলা হয়েছিল, গ্রামে শহরের সুবিধা দেয়া হবে। গত নির্বাচনী প্রচারণার সময় প্রধানমন্ত্রী শেখ হাসিনার দেয়া এ ঘোষণা বাস্তবায়নে প্রথম ধাপে ব্যয় হবে অন্তত ১৫০ কোটি টাকা। পরবর্তীতে ধাপে ধাপে অন্যান্য এলাকাতেও এ প্রকল্প বাস্তবায়ন করা হবে। এ প্রসঙ্গে স্থানীয় সরকার, পল্লী উন্নয়ন ও সমবায় (এলজিআরডি) প্রতিমন্ত্রী স্বপন ভট্টাচার্য্য যুগান্তরকে বলেন, গ্রামীণ জনগোষ্ঠীকে শহরের মতো উন্নত নাগরিক সুবিধা পৌঁছে দিতে আমরা ১৬টি গ্রামকে প্রাথমিকভাবে বেছে নিয়েছি। এটি ‘আদর্শ প্রকল্প’ হিসেবে বাস্তবায়ন করা হবে। তারপর ধাপে ধাপে এ প্রকল্প সম্প্রসারণ করা হবে বিভিন্ন এলাকায়। এ জন্য গ্রামের মানুষেরা কী কী সুবিধা চায়, তা তাদের কাছ থেকেই আমরা জেনে নিয়েছি। তাদের পরামর্শ ও মতামতের আলোকে এ প্রকল্প ডিজাইন করা হচ্ছে।\r\n\r\nএলজিআরডি মন্ত্রণালয়ের ‘পল্লী উন্নয়ন ও সমবায় বিভাগের’ তত্ত্বাবধানে এ প্রকল্প প্রণয়ন শুরু করেছে সমবায় অধিদফতর। এছাড়া একটি সরকারি প্রতিষ্ঠানকে ওই প্রকল্পের পরামর্শক প্রতিষ্ঠান নিয়োগ করেছে। পরামর্শক প্রতিষ্ঠানের সুপারিশ পাওয়ার পর ডিপিপি পূর্ণাঙ্গ করে অনুমোদনের জন্য মন্ত্রণালয়ে প্রেরণ করবে সমবায় অধিদফতর।\r\n\r\nপ্রকল্প অনুসারে, গ্রামের মানুষের নিজস্ব শক্তি জাগিয়ে তুলতে বাছাইকৃত গ্রামগুলোতে একটি সমবায় সমিতি প্রতিষ্ঠা করা হবে। ওই সমিতির মাধ্যমে গ্রামীণ উন্নয়ন, কর্মসংস্থান সৃষ্টি এবং কৃষকের পণ্য বাজারজাতকরণসহ প্রভৃতি কার্যক্রম পরিচালনা করা হবে। এছাড়া গ্রামের মানুষের মাঝে হৃদ্যতাপূর্ণ পরিবেশ জোরদার করতে প্রতিটি গ্রামে একটি করে কমিউনিটি সেন্টার নির্মাণ করা হবে। কমিউনিটি সেন্টারে ‘সমবায় সমিতির কার্যালয়’ থাকবে এবং সেটি বিয়েসহ পারিবারিক ও সাংস্কৃতিক কর্মকাণ্ডের জন্য ব্যবহার করা হবে।\r\n\r\nএ বিষয়ে সমবায় অধিদফতরের নিবন্ধক ও মহাপরিচালক আমিনুল ইসলাম যুগান্তরকে বলেন, বাছাইকৃত গ্রামগুলোতে শতভাগ বিদ্যুৎ এবং পাকা সড়ক যোগাযোগ নিশ্চিত করা হবে। এর বাইরে শিক্ষা, স্বাস্থ্য. পানি ও স্যানিটেশন এবং পরিবেশ বান্ধব বর্জ্য ব্যবস্থাপনা গড়ে তোলা হবে। তিনি বলেন, গ্রাম পর্যায়ে সমবায় সমিতি এবং নাগরিক সেবা কার্যক্রম তদারকি করতে উপজেলা পর্যায় থেকে একটি ব্যবস্থাপনা কমিটি গঠন করা হবে। সেখানে জনপ্রতিনিধি এবং উপজেলা পর্যায়ের স্থানীয় সরকার প্রকৌশল, শিক্ষা, স্বাস্থ্য বা প্রয়োজনে সরকারের অন্যান্য বিভাগকে অন্তর্ভুক্ত করা যাবে।\r\n\r\nএদিকে মুজিববর্ষে রাজধানীবাসীকে বিশুদ্ধ দুধ ও দুগ্ধজাত পণ্য সরবরাহ করতে ঢাকার ৫০টি থানায় ৫০টি ‘মিল্ক ভিটা কর্নার’ স্থাপন করা হবে। সংশ্লিষ্টরা জানান, প্রতিটি বিক্রয় কেন্দ্রে ২ জন করে নারী বিক্রয়কর্মী থাকবে। এর ফলে দুগ্ধখাত আরও বিকশিত হবে।\r\n\r\nএছাড়া মুজিববর্ষে গ্রামীণ জনগোষ্ঠীর জন্য একটি তাৎপর্যপূর্ণ প্রকল্প গ্রহণ করেছে বাংলাদেশ পল্লী উন্নয়ন বোর্ডের (বিআরডিবি)। প্রকল্পের অধীনে গ্রামীণ জনগণের অংশগ্রহণে মূল সড়ক থেকে সংযোগ সড়ক নির্মাণ করা হবে। গ্রামের মানুষ বাড়ি থেকে বের হয়ে কমিউনিটি সেন্টার, মসজিদ, মন্দির, কবর, শ্মশান, স্কুল ও অন্যান্য জায়গায় চলাচলের জন্য এ সংযোগ সড়ক ব্যবহার করবে। এসব সড়ক বেশি দীর্ঘ নয়। এ ধরনের সড়ক নির্মাণ কাজ এগিয়ে চলেছে।', '2020-03-01', 'published', '2020-03-01 13:26:05', '2020-04-08 21:03:59', 'images/upload/post/eyJpdiI6IkNBMkh4MzVDb1d1MUZpSEVTRXFxZnc9PSIsInZhbHVlIjoiT21Cc0hnUFBcL1lRMDd1VHU1ZFJNVnN5YUs3MlhaQVVrOHQyaHpBMDY3dzQ9IiwibWFjIjoiNDU1OTBlOGM0NWNjOWRmZmNkY2I5NjkzZGIxMzA0Y2RmMjNmZmMzMTVjMDkyNDAzODhiN2VmMTU1MWI0YTgyMiJ9.jpg', 0, 17),
(25, 1, 1, 'কারিগরি শিক্ষার মানোন্নয়ন: ভয়াবহ লুটপাট স্টেপ প্রকল্পে', 'কারিগরি শিক্ষার উন্নয়নে নেয়া স্কিলস অ্যান্ড ট্রেনিং এনহ্যান্সমেন্ট প্রজেক্ট (স্টেপ)-এর কাজে ভয়াবহ অনিয়ম-দুর্নীতি হয়েছে বলে অভিযোগ উঠেছে। প্রায় ১৮শ’ কোটি টাকার এই প্রকল্পের কয়েক কর্মকর্তার একটি চক্র নির্দিষ্ট প্রতিষ্ঠানের মাধ্যমে কেনাকাটার নামে অর্থ লোপাট করেছে।\r\n\r\nঅপকর্ম সহজ করতে পরিবারের সদস্যদের দিয়ে প্রতিষ্ঠা করা হয় নামসর্বস্ব কোম্পানি। পাশাপাশি প্রকল্পভুক্ত প্রতিষ্ঠানের সঙ্গে সহজে যোগাযোগের লক্ষ্যে কারিগরি শিক্ষা খাতের প্রভাবশালী একটি প্রতিষ্ঠানের কর্ণধারকে ওই কোম্পানিতে পরিচালক হিসেবে নেয়া হয়। এরপরই কাজের আড়ালে লুটপাটে মেতে ওঠেন চক্রের সদস্যরা। যুগান্তরের অনুসন্ধানে এসব তথ্য বেরিয়ে এসেছে।\r\n\r\nসূত্র জানায়, প্রকল্প বাস্তবায়নে নানা অনিয়ম-দুর্নীতির অভিযোগ মন্ত্রণালয়ের শীর্ষ কর্মকর্তারাও জানেন। এর পরিপ্রেক্ষিতে প্রকল্পভুক্ত ৩১ জেলার বিভিন্ন প্রতিষ্ঠানের জন্য কেনা যন্ত্রপাতি সম্পর্কে প্রতিবেদন দিতে ৩৮ কর্মকর্তার সমন্বয়ে ১১টি কমিটি গঠন করা হয়। অনুসন্ধান শেষে কমিটিগুলো প্রতিবেদন তৈরির কাজ করছে।\r\n\r\nএ প্রসঙ্গে জানতে চাইলে কারিগরি ও মাদ্রাসা বিভাগের সচিব মুনশী শাহাবুদ্দীন আহমেদ যুগান্তরকে বলেন, গত ডিসেম্বরে এ প্রকল্প শেষ হয়ে গেছে। তবে এখন পর্যন্ত পিসিআর (প্রকল্প সমাপনী প্রতিবেদন) পাইনি। এ কারণে প্রকল্প পরিচালক (পিডি) দু’বার অন্যত্র বদলি হলেও তাকে ছাড়পত্র দেয়া হয়নি। শেষ পর্যন্ত তৃতীয় বদলির আদেশের পর ছাড়পত্র দেয়া হয়েছে। তবে যদি প্রকল্পের কোনো কাজে কেউ অনিয়ম-দুর্নীতি করে থাকেন, তবে তার পার পাওয়ার সুযোগ নেই। সংশ্লিষ্টরা যেখানেই থাকুন না কেন, আইনত প্রকল্পের কাজের ব্যাপারে তিনি বা তারা দায়বদ্ধ। দোষী চিহ্নিত হলে সংশ্লিষ্টদের আইনের আওতায় আসতে হবে।\r\n\r\nদেশের কারিগরি শিক্ষার সার্বিক উন্নয়নে বাংলাদেশ সরকার, বিশ্বব্যাংক ও কানাডার যৌথ অর্থায়নে নেয়া এ প্রকল্পে বরাদ্দ ছিল এক হাজার ৭৮২ কোটি টাকা। প্রকল্পের একটি অংশ ছিল সরকারি-বেসরকারি পলিটেকনিক ইন্সটিউটে এককালীন মঞ্জুরি প্রদান। প্রথমে একেকটি পলিটেকনিক ইন্সটিটিউট ৯ কোটি টাকা করে মঞ্জুরি দেয়া হয়।\r\n\r\nএ নিয়ে প্রশ্ন উঠলেও প্রথমে কিছু প্রতিষ্ঠানকে নয় কোটি টাকা করে দেয়া হয়। কিন্তু পরের ধাপের প্রতিষ্ঠানগুলোকে বরাদ্দ কমিয়ে তিন কোটি টাকা করে দেয়া হয়। এ টাকায় বিভাগভিত্তিক ল্যাবরেটরি স্থাপন, শিক্ষক প্রশিক্ষণসহ একাধিক কাজ করার কথা; কিন্তু বাস্তবে বেশিরভাগ পলিটেকনিক ইন্সটিটিউট স্টেপ প্রকল্পের একটি চক্রের যোগসাজশে নামমাত্র ল্যাবরেটরি স্থাপন করে টাকা ছাড় করে নিয়েছে। আর যেসব প্রতিষ্ঠান এ চক্রের মাধ্যমে কাজ করেনি তাদের বিল আটকে রাখা হয়েছে।\r\n\r\nএ ছাড়া যারা সমঝোতায় কেনাকাটা করতে চায়নি, তাদের বরাদ্দ ফিরিয়ে নেয়া হয়েছে। আবার রফা না হওয়ায় প্রথমে কোনো প্রতিষ্ঠানকে সরবরাহের কাজ দেয়া হয়নি। রফা হওয়ার পর আগে অযোগ্য হলেও পরে একই কাগজপত্রে কাজ দেয়া হয়েছে। বিদেশে প্রশিক্ষণে পাঠানোর ক্ষেত্রেও আছে নানা অভিযোগ।\r\n\r\nঅভিযোগ উঠেছে- প্রকল্পের চক্রভুক্ত কর্মকর্তারা অনুদানপ্রাপ্ত প্রতিষ্ঠানগুলোকে নির্দিষ্ট সরবরাহকারীর কাছ থেকে ল্যাবরেটরির মালামাল কিনতে বলে দিতেন। কথামতো কাজ না করলে উন্মুক্ত দরপত্রের মাধ্যমে সব নিয়মকানুন মেনে মালামাল কিনলেও সংশ্লিষ্ট প্রতিষ্ঠানের বিল আটকে দেয়া হতো।\r\n\r\nআবার কোনো প্রতিষ্ঠান সিন্ডিকেটভুক্ত সরবরাহকারীর থেকে পণ্য নিতে রাজি হলে সেই প্রতিষ্ঠানের দরপত্রের ডকুমেন্টস তৈরি করে দিত সম্ভাব্য দরদাতা প্রতিষ্ঠান। ফলে এমন ক্ষেত্রে উন্মুক্ত দরপত্রেও সাধারণ দরদাতা কাজ পেত না। কারিগরি ও মাদ্রাসা বিভাগের সচিবের কাছে এমন বেশকিছু অভিযোগ পড়েছে।\r\n\r\nপ্রকল্প দফতরের পছন্দের প্রতিষ্ঠানকে কাজ না দেয়া প্রতিষ্ঠানের মধ্যে নারায়ণগঞ্জের ন্যাশনাল ইন্সটিটিউট অব ইঞ্জিনিয়ারিং অ্যান্ড টেকনোলজি (এনআইইটি) একটি। প্রতিষ্ঠানটির অধ্যক্ষ প্রকৌশলী মনজুরুল আলম অভিযোগ করেন, ল্যাবরেটরির জন্য যন্ত্রপাতি কেনার উদ্যোগ নেয়ার পরই প্রকল্প অফিস থেকে তাদের অফার করা হয় যে, মালামাল না কিনেও বিল সমন্বয় করার সুযোগ আছে। আবার আংশিক মালামাল কিনতে চাইলে সেটাও করতে পারেন। এ ক্ষেত্রে দু’ধরনের প্রতিষ্ঠানই প্রকল্পের কর্মকর্তারা দিতে পারবেন। তখন তারা মালামাল কেনার কথা জানালে একটি নির্দিষ্ট প্রতিষ্ঠানকে কাজ দিতে বলা হয়। এরপর উন্মুক্ত দরপত্রে গেলে তা দু’বার বাতিল করে।\r\n\r\nপরে তৃতীয়বার দরপত্র ডাকা হলেও প্রকল্প অফিসের কর্মকর্তাদের পছন্দের প্রতিষ্ঠান সর্বনিম্ন দরদাতা হয়নি। তখন তাদেরকে নথিপত্র পরিবর্তন করে প্রকল্পের পছন্দের প্রতিষ্ঠানকে কাজ দেয়ার জন্য চাপ দেয়া হয়।\r\n\r\nঅনিয়ম না করায় তার প্রতিষ্ঠানের কর্মকর্তাদের পরে প্রকল্প অফিসে মারধর করা হয়। এতেও তারা রাজি না হওয়ায় শেষ পর্যন্ত কাজের বিল আটকে দেয়া হয়। শেষ পর্যন্ত বিল না দিলেও দরপত্রে কেনা মালামাল ঠিকঠাক মতো ল্যাবরেটরিতে সংরক্ষিত থাকার ব্যাপারে প্রতিবেদন দিয়েছেন খোদ প্রকল্প পরিচালক। মারধরের ঘটনায় ৬ জুন থানায় জিডি করে প্রকল্প অফিস। আর এখন পর্যন্ত বিল না দেয়ার ঘটনায় পণ্য সরবরাহকারী তার প্রতিষ্ঠানসহ প্রকল্প ও মন্ত্রণালয়ের সংশ্লিষ্টদের আইনি নোটিশ করেছেন বলে জানান তিনি।\r\n\r\nআরও অভিযোগ পাওয়া গেছে, ঢাকার ওয়েস্টার্ন আইডিয়াল ইন্সটিটিউট (ডব্লিউআইআই) যথাসময়ে দরপত্রের মাধ্যমে যন্ত্রপাতি কিনেছে; কিন্তু প্রকল্প অফিসের সঙ্গে সুসম্পর্ক না থাকায় ওই কাজের সরবরাহকারী বিল পায়নি।\r\n\r\nপ্রতিষ্ঠানটির অধ্যক্ষ প্রকৌশলী মো. সিরাজুল ইসলাম বলেন, মালামাল স্পেসিফিকেশন ও পরিমাণ অনুযায়ী কেনা হয়েছে কি না, তা খোদ প্রকল্প পরিচালক অন্যদের নিয়ে পরিদর্শন করে দেখেছেন। এ সংক্রান্ত প্রতিবেদনে সবকিছু ঠিকমতো সংরক্ষিত আছে বলে প্রতিবেদনে উল্লেখও করেছেন। কিন্তু প্রকল্পের ব্যক্তিদের পছন্দের সরবরাহকারীকে কাজ না দেয়ায় তার বিল আটকে দেয়া হয়েছে। এ ঘটনায়ও সরবরাহকারী আইনি ব্যবস্থা নিয়েছেন বলে জানান এই অধ্যক্ষ।\r\n\r\nঅভিযোগ পাওয়া গেছে, নির্দিষ্ট সরবরাহকারীকে কাজ দিতে বিভিন্ন প্রতিষ্ঠানকে ফোন করতেন প্রকল্পের কর্মকর্তা এইচএম কবির হোসেন। এ ব্যাপারে তিনি রোববার যুগান্তরকে বলেন, এগুলো (কাউকে কাজ দিতে বলা) আমরা করিনি। তবে প্রকল্পের দরপত্র কাজে বিভিন্ন প্রতিষ্ঠান অংশ নেয়। তখন অনেক সময়ে ঝামেলা হয়। সেই পরিস্থিতিতে ঊর্ধ্বতন কর্তৃপক্ষের নির্দেশে দু-একটি ক্ষেত্রে আমাকে ভূমিকা রাখতে হয়েছে। কিন্তু প্রকল্পের সিদ্ধান্ত দেন পিডি। আমরা সিদ্ধান্তদাতা নই। তার নির্দেশেই কাজ করেছি। এক প্রশ্নের জবাবে তিনি বলেন, যদি প্রকল্পের কাজে ব্যক্তিগতভাবে কারও বিরুদ্ধে কোনো অন্যায় প্রমাণিত হয় তাহলে ব্যবস্থা নেয়া উচিত।\r\n\r\nসূত্র জানায়, বনিবনা হলে জাল কাগজে কাজ দেয়ার অভিযোগও আছে। এসএম ইন্টারন্যাশনাল নামক একটি সরবরাহকারী প্রতিষ্ঠানের কাগজপত্র জাল থাকায় দিনাজপুর টেকনিক্যাল ট্রেনিং সেন্টারের প্রায় কোটি টাকার কাজে সর্বনিম্ন দরদাতা হওয়া সত্ত্বেও কাজ দেয়া হয়নি। অথচ ওই সরবরাহকারী রহস্যজনক কারণে একই কাগজপত্রে পরে মৃণাল কম্পিউটার নামক আরেকটি প্রতিষ্ঠানের ১৮ লাখ টাকার কাজ পায়।\r\n\r\nআবার রহস্যজনক কারণে বরাদ্দ ফেরত নেয়ার অভিযোগ আছে। সিলেট টেকনিক্যাল ট্রেনিং সেন্টারকে (টিটিসি) উন্নয়নের জন্য প্রকল্প থেকে ৩ কোটি টাকা দেয়া হয়েছিল। এর মধ্যে মালামাল কেনা সংক্রান্ত ৮০ লাখ টাকার একটি প্যাকেজ বরাদ্দ থেকে বাতিল করা হয়েছিল।\r\n\r\nঅভিযোগ আছে- সেখানে প্রতিষ্ঠানটির পুরোনো মালামালই স্পেসিফিকেশন করতে বলা হয়েছিল। কিন্তু প্রকল্পের পরামর্শকরা এতে রাজি হননি। এ কারণে একজন উপ-প্রকল্প পরিচালক দুর্ব্যবহারও করেন বলে জানান এ প্রকল্পের এক পরামর্শক।\r\n\r\nএ ব্যাপারে টিটিসির অধ্যক্ষ ওয়ালিউল্লা মোল্লা বলেন, উন্মুক্ত দরপত্র করে তারা কাগজপত্র প্রকল্প অফিসে পাঠান। পরে তাদের জানানো হয় যে, সর্বনিম্ন দরদাতা প্রতিষ্ঠান কাজ পাওয়ার উপযোগী। কিন্তু তার কাগজপত্রে ত্রুটি আছে, তাই তাকে কাজ দেয়া যাবে না। এরপর বরাদ্দ ফিরিয়ে নেয়া হয়।\r\n\r\nপছন্দের প্রতিষ্ঠান : অনুসন্ধানে বেরিয়ে এসেছে আরও কিছু চাঞ্চল্যকর তথ্য। স্টেপ প্রকল্পটি নিয়ন্ত্রণ করত তিন কর্মকর্তার একটি চক্র। তাদের ছিল পছন্দের কয়েকটি সরবরাহকারী প্রতিষ্ঠান। ঘুরেফিরে ওইসব প্রতিষ্ঠানই পেয়েছে বেশিরভাগ কাজ। এমন প্রতিষ্ঠানের মধ্যে জনি এন্টারপ্রাইজ, ইউনিক বিজনেস সিস্টেম, জননী এন্টারপ্রাইজ, অ্যাডভান্স টেকনোলজি কনসোর্টিয়াম, কম্পিউটার ইন্টিগ্রেটেড সার্ভিস এবং টাচ অ্যান্ড সলভ অন্যতম। এসব প্রতিষ্ঠানের একটির সঙ্গে প্রকল্পের হিসাবরক্ষণ কর্মকর্তার পরিবারের এক সদস্য জড়িত। সংশ্লিষ্ট সরবরাহকারীরা যেসব প্রতিষ্ঠানে কাজ করেছে সেখানে তাদের বিরুদ্ধে নিবিড় তদন্তের দাবি উঠেছে।\r\n\r\nআরও কিছু অভিযোগ : উল্লিখিত সিন্ডিকেটের বিরুদ্ধে শিক্ষকদের বিদেশে প্রশিক্ষণে পাঠানোর নামে মোটা অঙ্কের অর্থ গ্রহণের অভিযোগ রয়েছে। এমনকি এক প্রতিষ্ঠানের শিক্ষককে অন্য প্রতিষ্ঠানের নামেও প্রশিক্ষণে পাঠানোর অভিযোগ আছে। প্রকল্পের উপ-পরিচালক পদটি ৫ম গ্রেডের। কিন্তু সিন্ডিকেটের সদস্য ৭ম গ্রেডের কর্মকর্তাকে ওই পদে বসিয়ে রাখা হয়। ভাতাও দেয়া হয় ৫ম গ্রেডের হিসাবে। পাশাপাশি ঢাকায় কর্মরত থেকে নিয়েছেন পার্বত্য ভাতা। এভাবে লুট হয়েছে প্রকল্পের অর্থ।\r\n\r\n১১ কমিটি গঠন : নানা অভিযোগের কারণে ৩১ জেলার পলিটেকনিক ইন্সটিটিউটগুলোর ক্রয়কৃত মালামাল পরিদর্শন ও প্রতিবেদন দিতে গত ২৪ নভেম্বর ১১টি কমিটি করা হয়। ১৫ ডিসেম্বরের মধ্যে প্রতিবেদন দিতে বলা হয় এসব কমিটিকে। তবে ২৭ ফেব্রুয়ারি পর্যন্ত কোনো কমিটি প্রতিবেদন দিতে পারেনি বলে জানা গেছে।\r\n\r\nএ প্রসঙ্গে একটি কমিটির প্রধান অতিরিক্ত সচিব বিল্লাল হোসেন গত ৬ ফেব্রুয়ারি যুগান্তরকে বলেন, আমাকে ৫টি জেলার প্রতিষ্ঠান পরিদর্শন করে প্রতিবেদন দিতে বলা হয়েছে। প্রতিষ্ঠানগুলো কোটি কোটি টাকার মালামাল কিনেছে। মালামালের মধ্যে অনেক ছোট ছোট যন্ত্রপাতিও আছে। কোনোরকম ঘুরে প্রতিবেদন দেয়া ঠিক হবে না। তাই কাজ শেষ করতে একটু সময় লাগছে।\r\n\r\nসংশ্লিষ্টরা বলছেন, যোগসাজশে যেসব প্রতিষ্ঠানে মালামাল কেনা হয়েছে, পরিদর্শনে সেখানে কেনাকাটার আসল তথ্য বের হবে না। কারণ, অনুমোদনের জন্য প্রতিটি প্রতিষ্ঠানে আগে থেকেই মালামাল ছিল। তাই নতুন মালামাল শুধু দরপত্রের কাগজ দেখে চেনা যাবে না। আগের মালের ইনভেন্টরি, বিক্রি করে থাকলে তার টেন্ডার ও বিল-ভাউচার ইত্যাদি নিরীক্ষা করতে হবে। সবচেয়ে বড় কথা হচ্ছে, সংশ্লিষ্ট বিশেষজ্ঞ থাকতে হবে কমিটিতে। কিন্তু এসব ক্ষেত্রে তা ছিল না।\r\n\r\nপ্রতিবেদনগুলো সংকলনের দায়িত্বপ্রাপ্ত অতিরিক্ত সচিব অজিত কুমার ঘোষ বৃহস্পতিবার যুগান্তরকে বলেন, আমি নিজেও একটি টিমে আছি। দুটি প্রতিষ্ঠান পরিদর্শন করেছি। কেনাকাটার কাজ বাস্তবায়নে কিছু প্রক্রিয়াগত ত্রুটি হয়েছে। দেখা গেছে, কোনো কোনো আইটেমের কেনাকাটার প্রাক্কলন হয়েছে আইডিবি ভবনে। এমন ক্ষেত্রে প্রাক্কলনের ৪০ শতাংশ অর্থেই পণ্য পাওয়া গেছে। এ ছাড়া তেমন কোনো অনিয়ম পাইনি। তিনি বলেন, তবে এটা ঠিক যে, আমাদের কমিটিতে কোনো বিশেষজ্ঞ ছিল না। যে পরিমাণের কেনাকাটা, তার খুঁত বের করতে কমিটিতে বিশেষজ্ঞ দরকার।\r\n\r\nপ্রকল্প পরিচালকের বক্তব্য : এসব অভিযোগের ব্যাপারে দু’দফায় বক্তব্য নেয়া হয় প্রকল্প পরিচালক এবিএম আজাদের। প্রথমে গত ২৮ অক্টোবর আলাপকালে অভিযোগের ব্যাপারে জানতে চাইলে লিখিত প্রশ্ন চান। পাশাপাশি কথা বলতে ও তথ্য দিতে অস্বীকৃতি জানান। পরে একটা পর্যায়ে তিনি বলেন, নির্দিষ্ট প্রতিষ্ঠানকে কাজ দিতে কাউকে অনুরোধ করেননি। কেউ চাপ দিয়ে থাকলে শাস্তিযোগ্য অপরাধ করেছে। তার দফতর থেকে কে কাকে (চাপ) দিয়েছে তা সুনির্দিষ্ট করে বলার পরামর্শ দিয়ে তিনি বলেন, তারা প্রতিষ্ঠানগুলোকে থোক বরাদ্দ দেন। প্রকল্প অফিস কোনো প্রতিষ্ঠানের দরপত্র করেন না।\r\n\r\nনির্দিষ্ট প্রতিষ্ঠানকে কাজ না দেয়ায় বিল আটকে রাখা সম্পর্কে তিনি বলেন, এ ব্যাপারে আপনি ডকুমেন্ট দেন। এরপর আমি বলব। পাশাপাশি তিনি বলেন, আমাকে তো পিপিআর (সরকারি ক্রয় আইন) অনুসরণ করতে হয়। এ প্রসঙ্গে তিনি আরও বলেন, এ বিষয়টা দেখেন আমার ডিপিডি। আমি গোটা প্রকল্প দেখি। একটা কেনাকাটার কাজে প্রকল্পের বিভিন্ন পর্যায়ের কর্মকর্তারা জড়িত থাকেন। তারা যখন কোনো বিষয় নেতিবাচক বলেন তখন সেটা আমি দেখি এবং নেতিবাচক হলে তা হ্যাঁ বলার কোনো কারণ নেই। তেমনি ইতিবাচক বললে এবং সেরকম হলে ইতিবাচক বলি। এক প্রশ্নের জবাবে তিনি বলেন, পিপিআর অনুযায়ী শুধু সর্বোচ্চ দরদাতাই যে কাজ পাবে সেটা একমাত্র ক্রাইটেরিয়া নয়।\r\n\r\nজাল সনদের কারণে প্রথমে দরপত্রের কাজ না দিলেও পরে একই প্রতিষ্ঠানকে একই ধরনের কাগজপত্রের ভিত্তিতে সরবরাহের কাজ দেয়ার বিষয়টি জানেন কি না- এমন প্রশ্নের জবাবে প্রথমে তিনি বলেন, এ মুহূর্তে আমি এ বিষয়ে কিছু বলতে পারব না। পরেই আবার বলেন, না জানার কোনো কারণ নেই। আমার রেকর্ডেই সেটা বলা আছে। রেকর্ড না দেখে বলা যাবে না। পরে রোববার তার সঙ্গে ফের কথা হয়। তখনও তিনি অভিযোগের ব্যাপারে উল্লিখিত কথাগুলো আবারও বর্ণনা করেন। বিভিন্ন প্রতিষ্ঠানের বিল আটকে দেয়া সম্পর্কে এদিন জানতে চাইলে বলেন, প্রক্রিয়াগত জটিলতার কারণে বিল দেয়া যায়নি।', '2020-03-01', 'published', '2020-03-01 13:27:06', '2020-11-15 00:11:21', 'images/upload/post/eyJpdiI6IlcxWFdMbFZGUGkxdWlrTHB5dVwvVjhBPT0iLCJ2YWx1ZSI6IjAzOWx1T3prYXdZUzAzbktQeE1zclBkWHYzd2VvMlNWXC82OWVKb0FZR1BVPSIsIm1hYyI6ImVhMzY1MTk5Y2QwODlhNjkwZDUxMjUyN2ZlNjQ3N2I0ZTYxYzcwMDY5NjVlOGRjNDRlYzllNzUyN2YwMmUxYzkifQ==.jpg', 0, 13);

